INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16492, 'nvi', 'Provérbios', 'pv', 4, 1, 'Ouçam, meus filhos, a instrução de um pai; estejam atentos, e obterão discernimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16493, 'nvi', 'Provérbios', 'pv', 4, 2, 'O ensino que lhes ofereço é bom; por isso não abandonem a minha instrução.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16494, 'nvi', 'Provérbios', 'pv', 4, 3, 'Quando eu era menino, ainda pequeno, em companhia de meu pai, um filho muito especial para minha mãe,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16495, 'nvi', 'Provérbios', 'pv', 4, 4, 'ele me ensinava e me dizia: "Apegue-se às minhas palavras de todo o coração; obedeça aos meus mandamentos, e você terá vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16496, 'nvi', 'Provérbios', 'pv', 4, 5, 'Procure obter sabedoria e entendimento; não se esqueça das minhas palavras nem delas se afaste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16497, 'nvi', 'Provérbios', 'pv', 4, 6, 'Não abandone a sabedoria, e ela o protegerá; ame-a, e ela cuidará de você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16498, 'nvi', 'Provérbios', 'pv', 4, 7, 'O conselho da sabedoria é: procure obter sabedoria; use tudo que você possui para adquirir entendimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16499, 'nvi', 'Provérbios', 'pv', 4, 8, 'Dedique alta estima à sabedoria, e ela o exaltará; abrace-a, e ela o honrará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16500, 'nvi', 'Provérbios', 'pv', 4, 9, 'Ela porá um belo diadema sobre a sua cabeça e lhe dará de presente uma coroa de esplendor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16501, 'nvi', 'Provérbios', 'pv', 4, 10, 'Ouça, meu filho, e aceite o que digo, e você terá vida longa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16502, 'nvi', 'Provérbios', 'pv', 4, 11, 'Eu o conduzi pelo caminho da sabedoria e o encaminhei por veredas retas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16503, 'nvi', 'Provérbios', 'pv', 4, 12, 'Assim, quando você por elas seguir, não encontrará obstáculos; quando correr, não tropeçará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16504, 'nvi', 'Provérbios', 'pv', 4, 13, 'Apegue-se à instrução, não a abandone; guarde-a bem, pois dela depende a sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16505, 'nvi', 'Provérbios', 'pv', 4, 14, 'Não siga pela vereda dos ímpios nem ande no caminho dos maus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16506, 'nvi', 'Provérbios', 'pv', 4, 15, 'Evite-o, não passe por ele; afaste-se e não se detenha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16507, 'nvi', 'Provérbios', 'pv', 4, 16, 'Pois eles não conseguem dormir enquanto não fazem o mal; perdem o sono se não causarem a ruína de alguém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16508, 'nvi', 'Provérbios', 'pv', 4, 17, 'Pois eles se alimentam de maldade, e se embriagam de violência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16509, 'nvi', 'Provérbios', 'pv', 4, 18, 'A vereda do justo é como a luz da alvorada, que brilha cada vez mais até à plena claridade do dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16510, 'nvi', 'Provérbios', 'pv', 4, 19, 'Mas o caminho dos ímpios é como densas trevas; nem sequer sabem em que tropeçam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16511, 'nvi', 'Provérbios', 'pv', 4, 20, 'Meu filho, escute o que lhe digo; preste atenção às minhas palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16512, 'nvi', 'Provérbios', 'pv', 4, 21, 'Nunca as perca de vista; guarde-as no fundo do coração,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16513, 'nvi', 'Provérbios', 'pv', 4, 22, 'pois são vida para quem as encontra e saúde para todo o seu ser.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16514, 'nvi', 'Provérbios', 'pv', 4, 23, 'Acima de tudo, guarde o seu coração, pois dele depende toda a sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16515, 'nvi', 'Provérbios', 'pv', 4, 24, 'Afaste da sua boca as palavras perversas; fique longe dos seus lábios a maldade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16516, 'nvi', 'Provérbios', 'pv', 4, 25, 'Olhe sempre para a frente, mantenha o olhar fixo no que está adiante de você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16517, 'nvi', 'Provérbios', 'pv', 4, 26, 'Veja bem por onde anda, e os seus passos serão seguros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16518, 'nvi', 'Provérbios', 'pv', 4, 27, 'Não se desvie nem para a direita nem para a esquerda; afaste os seus pés da maldade.');
