INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16640, 'nvi', 'Provérbios', 'pv', 9, 1, 'A sabedoria construiu sua casa; ergueu suas sete colunas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16641, 'nvi', 'Provérbios', 'pv', 9, 2, 'Matou animais para a refeição, preparou seu vinho e arrumou sua mesa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16642, 'nvi', 'Provérbios', 'pv', 9, 3, 'E enviou as servas para fazerem convites desde o ponto mais alto da cidade, clamando:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16643, 'nvi', 'Provérbios', 'pv', 9, 4, '"Venham todos os inexperientes! " Aos que não têm bom senso ela diz:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16644, 'nvi', 'Provérbios', 'pv', 9, 5, '"Venham comer a minha comida e beber o vinho que preparei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16645, 'nvi', 'Provérbios', 'pv', 9, 6, 'Deixem a insensatez, e vocês terão vida; andem pelo caminho do entendimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16646, 'nvi', 'Provérbios', 'pv', 9, 7, '"Quem corrige o zombador traz sobre si o insulto; quem repreende o ímpio mancha o próprio nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16647, 'nvi', 'Provérbios', 'pv', 9, 8, 'Não repreenda o zombador, caso contrário ele o odiará; repreenda o sábio, e ele o amará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16648, 'nvi', 'Provérbios', 'pv', 9, 9, 'Instrua o homem sábio, e ele será ainda mais sábio; ensine o homem justo, e ele aumentará o seu saber.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16649, 'nvi', 'Provérbios', 'pv', 9, 10, '"O temor do Senhor é o princípio da sabedoria, e o conhecimento do Santo é entendimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16650, 'nvi', 'Provérbios', 'pv', 9, 11, 'Pois por meu intermédio os seus dias serão multiplicados, e o tempo da sua vida se prolongará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16651, 'nvi', 'Provérbios', 'pv', 9, 12, 'Se você for sábio, o benefício será seu; se for zombador, sofrerá as conseqüências".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16652, 'nvi', 'Provérbios', 'pv', 9, 13, 'A insensatez é pura exibição, sedução e ignorância.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16653, 'nvi', 'Provérbios', 'pv', 9, 14, 'Sentada à porta de sua casa, no ponto mais alto da cidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16654, 'nvi', 'Provérbios', 'pv', 9, 15, 'clama aos que passam por ali seguindo o seu caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16655, 'nvi', 'Provérbios', 'pv', 9, 16, '"Venham todos os inexperientes! " Aos que não têm bom senso ela diz:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16656, 'nvi', 'Provérbios', 'pv', 9, 17, '"A água roubada é doce, e o pão que se come escondido é saboroso! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16657, 'nvi', 'Provérbios', 'pv', 9, 18, 'Mas eles nem imaginam que ali estão os espíritos dos mortos, que os seus convidados estão nas profundezas da sepultura.');
