INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27932, 'nvi', 'Romanos', 'rm', 1, 1, 'Paulo, servo de Cristo Jesus, chamado para ser apóstolo, separado para o evangelho de Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27933, 'nvi', 'Romanos', 'rm', 1, 2, 'o qual foi prometido por ele de antemão por meio dos seus profetas nas Escrituras Sagradas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27934, 'nvi', 'Romanos', 'rm', 1, 3, 'acerca de seu Filho, que, como homem, era descendente de Davi,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27935, 'nvi', 'Romanos', 'rm', 1, 4, 'e que mediante o Espírito de santidade foi declarado Filho de Deus com poder, pela sua ressurreição dentre os mortos: Jesus Cristo, nosso Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27936, 'nvi', 'Romanos', 'rm', 1, 5, 'Por meio dele e por causa do seu nome, recebemos graça e apostolado para chamar dentre todas as nações um povo para a obediência que vem pela fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27937, 'nvi', 'Romanos', 'rm', 1, 6, 'E vocês também estão entre os chamados para pertencerem a Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27938, 'nvi', 'Romanos', 'rm', 1, 7, 'A todos os que em Roma são amados de Deus e chamados para serem santos: A vocês, graça e paz da parte de Deus nosso Pai e do Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27939, 'nvi', 'Romanos', 'rm', 1, 8, 'Antes de tudo, sou grato a meu Deus, mediante Jesus Cristo, por todos vocês, porque em todo o mundo está sendo anunciada a fé que vocês têm.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27940, 'nvi', 'Romanos', 'rm', 1, 9, 'Deus, a quem sirvo de todo o coração pregando o evangelho de seu Filho, é minha testemunha de como sempre me lembro de vocês');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27941, 'nvi', 'Romanos', 'rm', 1, 10, 'em minhas orações; e peço que agora, finalmente, pela vontade de Deus, seja-me aberto o caminho para que eu possa visitá-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27942, 'nvi', 'Romanos', 'rm', 1, 11, 'Anseio vê-los, a fim de compartilhar com vocês algum dom espiritual, para fortalecê-los,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27943, 'nvi', 'Romanos', 'rm', 1, 12, 'isto é, para que eu e vocês sejamos mutuamente encorajados pela fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27944, 'nvi', 'Romanos', 'rm', 1, 13, 'Quero que vocês saibam, irmãos, que muitas vezes planejei visitá-los, mas fui impedido de fazê-lo até agora. Meu propósito é colher algum fruto entre vocês, assim como tenho colhido entre os demais gentios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27945, 'nvi', 'Romanos', 'rm', 1, 14, 'Sou devedor tanto a gregos como a bárbaros, tanto a sábios como a ignorantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27946, 'nvi', 'Romanos', 'rm', 1, 15, 'Por isso estou disposto a pregar o evangelho também a vocês que estão em Roma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27947, 'nvi', 'Romanos', 'rm', 1, 16, 'Não me envergonho do evangelho, porque é o poder de Deus para a salvação de todo aquele que crê: primeiro do judeu, depois do grego.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27948, 'nvi', 'Romanos', 'rm', 1, 17, 'Porque no evangelho é revelada a justiça de Deus, uma justiça que do princípio ao fim é pela fé, como está escrito: "O justo viverá pela fé".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27949, 'nvi', 'Romanos', 'rm', 1, 18, 'Portanto, a ira de Deus é revelada do céu contra toda impiedade e injustiça dos homens que suprimem a verdade pela injustiça,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27950, 'nvi', 'Romanos', 'rm', 1, 19, 'pois o que de Deus se pode conhecer é manifesto entre eles, porque Deus lhes manifestou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27951, 'nvi', 'Romanos', 'rm', 1, 20, 'Pois desde a criação do mundo os atributos invisíveis de Deus, seu eterno poder e sua natureza divina, têm sido vistos claramente, sendo compreendidos por meio das coisas criadas, de forma que tais homens são indesculpáveis;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27952, 'nvi', 'Romanos', 'rm', 1, 21, 'porque, tendo conhecido a Deus, não o glorificaram como Deus, nem lhe renderam graças, mas os seus pensamentos tornaram-se fúteis e os seus corações insensatos se obscureceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27953, 'nvi', 'Romanos', 'rm', 1, 22, 'Dizendo-se sábios, tornaram-se loucos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27954, 'nvi', 'Romanos', 'rm', 1, 23, 'e trocaram a glória do Deus imortal por imagens feitas segundo a semelhança do homem mortal, bem como de pássaros, quadrúpedes e répteis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27955, 'nvi', 'Romanos', 'rm', 1, 24, 'Por isso Deus os entregou à impureza sexual, segundo os desejos pecaminosos dos seus corações, para a degradação dos seus corpos entre si.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27956, 'nvi', 'Romanos', 'rm', 1, 25, 'Trocaram a verdade de Deus pela mentira, e adoraram e serviram a coisas e seres criados, em lugar do Criador, que é bendito para sempre. Amém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27957, 'nvi', 'Romanos', 'rm', 1, 26, 'Por causa disso Deus os entregou a paixões vergonhosas. Até suas mulheres trocaram suas relações sexuais naturais por outras, contrárias à natureza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27958, 'nvi', 'Romanos', 'rm', 1, 27, 'Da mesma forma, os homens também abandonaram as relações naturais com as mulheres e se inflamaram de paixão uns pelos outros. Começaram a cometer atos indecentes, homens com homens, e receberam em si mesmos o castigo merecido pela sua perversão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27959, 'nvi', 'Romanos', 'rm', 1, 28, 'Além do mais, visto que desprezaram o conhecimento de Deus, ele os entregou a uma disposição mental reprovável, para praticarem o que não deviam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27960, 'nvi', 'Romanos', 'rm', 1, 29, 'Tornaram-se cheios de toda sorte de injustiça, maldade, ganância e depravação. Estão cheios de inveja, homicídio, rivalidades, engano e malícia. São bisbilhoteiros,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27961, 'nvi', 'Romanos', 'rm', 1, 30, 'caluniadores, inimigos de Deus, insolentes, arrogantes e presunçosos; inventam maneiras de praticar o mal; desobedecem a seus pais;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27962, 'nvi', 'Romanos', 'rm', 1, 31, 'são insensatos, desleais, sem amor pela família, implacáveis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27963, 'nvi', 'Romanos', 'rm', 1, 32, 'Embora conheçam o justo decreto de Deus, de que as pessoas que praticam tais coisas merecem a morte, não somente continuam a praticá-las, mas também aprovam aqueles que as praticam.');
