INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28190, 'nvi', 'Romanos', 'rm', 10, 1, 'Irmãos, o desejo do meu coração e a minha oração a Deus pelos israelitas é que eles sejam salvos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28191, 'nvi', 'Romanos', 'rm', 10, 2, 'Pois posso testemunhar que eles têm zelo por Deus, mas o seu zelo não se baseia no conhecimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28192, 'nvi', 'Romanos', 'rm', 10, 3, 'Porquanto, ignorando a justiça que vem de Deus e procurando estabelecer a sua própria, não se submeteram à justiça de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28193, 'nvi', 'Romanos', 'rm', 10, 4, 'Porque o fim da lei é Cristo, para a justificação de todo o que crê.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28194, 'nvi', 'Romanos', 'rm', 10, 5, 'Moisés descreve desta forma a justiça que vem da lei: "O homem que fizer estas coisas viverá por meio delas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28195, 'nvi', 'Romanos', 'rm', 10, 6, 'Mas a justiça que vem da fé diz: "Não diga em seu coração: ‘Quem subirá ao céu? ’ ( isto é, para fazer Cristo descer )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28196, 'nvi', 'Romanos', 'rm', 10, 7, 'ou ‘Quem descerá ao abismo? ’ " ( isto é, para fazer subir Cristo dentre os mortos ).');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28197, 'nvi', 'Romanos', 'rm', 10, 8, 'Mas o que ela diz? "A palavra está perto de você; está em sua boca e em seu coração", isto é, a palavra da fé que estamos proclamando:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28198, 'nvi', 'Romanos', 'rm', 10, 9, 'Se você confessar com a sua boca que Jesus é Senhor e crer em seu coração que Deus o ressuscitou dentre os mortos, será salvo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28199, 'nvi', 'Romanos', 'rm', 10, 10, 'Pois com o coração se crê para justiça, e com a boca se confessa para salvação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28200, 'nvi', 'Romanos', 'rm', 10, 11, 'Como diz a Escritura: "Todo o que nele confia jamais será envergonhado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28201, 'nvi', 'Romanos', 'rm', 10, 12, 'Não há diferença entre judeus e gentios, pois o mesmo Senhor é Senhor de todos e abençoa ricamente todos os que o invocam,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28202, 'nvi', 'Romanos', 'rm', 10, 13, 'porque "todo aquele que invocar o nome do Senhor será salvo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28203, 'nvi', 'Romanos', 'rm', 10, 14, 'Como, pois, invocarão aquele em quem não creram? E como crerão naquele de quem não ouviram falar? E como ouvirão, se não houver quem pregue?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28204, 'nvi', 'Romanos', 'rm', 10, 15, 'E como pregarão, se não forem enviados? Como está escrito: "Como são belos os pés dos que anunciam boas novas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28205, 'nvi', 'Romanos', 'rm', 10, 16, 'No entanto, nem todos os israelitas aceitaram as boas novas. Pois Isaías diz: "Senhor, quem creu em nossa mensagem? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28206, 'nvi', 'Romanos', 'rm', 10, 17, 'Conseqüentemente, a fé vem por ouvir a mensagem, e a mensagem é ouvida mediante a palavra de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28207, 'nvi', 'Romanos', 'rm', 10, 18, 'Mas eu pergunto: Eles não a ouviram? Claro que sim: "A sua voz ressoou por toda a terra, e as suas palavras, até os confins do mundo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28208, 'nvi', 'Romanos', 'rm', 10, 19, 'Novamente pergunto: Será que Israel não entendeu? Moisés foi o primeiro que disse: "Farei que tenham ciúmes de quem não é meu povo; eu os provocarei à ira por meio de um povo sem entendimento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28209, 'nvi', 'Romanos', 'rm', 10, 20, 'E Isaías diz ousadamente: "Fui achado por aqueles que não me procuravam; revelei-me àqueles que não perguntavam por mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28210, 'nvi', 'Romanos', 'rm', 10, 21, 'Mas a respeito de Israel, ele diz: "O tempo todo estendi as mãos a um povo desobediente e rebelde".');
