INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28247, 'nvi', 'Romanos', 'rm', 12, 1, 'Portanto, irmãos, rogo-lhes pelas misericórdias de Deus que se ofereçam em sacrifício vivo, santo e agradável a Deus; este é o culto racional de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28248, 'nvi', 'Romanos', 'rm', 12, 2, 'Não se amoldem ao padrão deste mundo, mas transformem-se pela renovação da sua mente, para que sejam capazes de experimentar e comprovar a boa, agradável e perfeita vontade de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28249, 'nvi', 'Romanos', 'rm', 12, 3, 'Pois pela graça que me foi dada digo a todos vocês: ninguém tenha de si mesmo um conceito mais elevado do que deve ter; mas, pelo contrário, tenha um conceito equilibrado, de acordo com a medida da fé que Deus lhe concedeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28250, 'nvi', 'Romanos', 'rm', 12, 4, 'Assim como cada um de nós tem um corpo com muitos membros e esses membros não exercem todos a mesma função,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28251, 'nvi', 'Romanos', 'rm', 12, 5, 'assim também em Cristo nós, que somos muitos, formamos um corpo, e cada membro está ligado a todos os outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28252, 'nvi', 'Romanos', 'rm', 12, 6, 'Temos diferentes dons, de acordo com a graça que nos foi dada. Se alguém tem o dom de profetizar, use-o na proporção da sua fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28253, 'nvi', 'Romanos', 'rm', 12, 7, 'Se o seu dom é servir, sirva; se é ensinar, ensine;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28254, 'nvi', 'Romanos', 'rm', 12, 8, 'se é dar ânimo, que assim faça; se é contribuir, que contribua generosamente; se é exercer liderança, que a exerça com zelo; se é mostrar misericórdia, que o faça com alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28255, 'nvi', 'Romanos', 'rm', 12, 9, 'O amor deve ser sincero. Odeiem o que é mau; apeguem-se ao que é bom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28256, 'nvi', 'Romanos', 'rm', 12, 10, 'Dediquem-se uns aos outros com amor fraternal. Prefiram dar honra aos outros mais do que a si próprios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28257, 'nvi', 'Romanos', 'rm', 12, 11, 'Nunca lhes falte o zelo, sejam fervorosos no espírito, sirvam ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28258, 'nvi', 'Romanos', 'rm', 12, 12, 'Alegrem-se na esperança, sejam pacientes na tribulação, perseverem na oração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28259, 'nvi', 'Romanos', 'rm', 12, 13, 'Compartilhem o que vocês têm com os santos em suas necessidades. Pratiquem a hospitalidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28260, 'nvi', 'Romanos', 'rm', 12, 14, 'Abençoem aqueles que os perseguem; abençoem, e não os amaldiçoem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28261, 'nvi', 'Romanos', 'rm', 12, 15, 'Alegrem-se com os que se alegram; chorem com os que choram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28262, 'nvi', 'Romanos', 'rm', 12, 16, 'Tenham uma mesma atitude uns para com os outros. Não sejam orgulhosos, mas estejam dispostos a associar-se a pessoas de posição inferior. Não sejam sábios aos seus próprios olhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28263, 'nvi', 'Romanos', 'rm', 12, 17, 'Não retribuam a ninguém mal por mal. Procurem fazer o que é correto aos olhos de todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28264, 'nvi', 'Romanos', 'rm', 12, 18, 'Façam todo o possível para viver em paz com todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28265, 'nvi', 'Romanos', 'rm', 12, 19, 'Amados, nunca procurem vingar-se, mas deixem com Deus a ira, pois está escrito: "Minha é a vingança; eu retribuirei", diz o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28266, 'nvi', 'Romanos', 'rm', 12, 20, 'Pelo contrário: "Se o seu inimigo tiver fome, dê-lhe de comer; se tiver sede, dê-lhe de beber. Fazendo isso, você amontoará brasas vivas sobre a cabeça dele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28267, 'nvi', 'Romanos', 'rm', 12, 21, 'Não se deixem vencer pelo mal, mas vençam o mal com o bem.');
