INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28282, 'nvi', 'Romanos', 'rm', 14, 1, 'Aceitem o que é fraco na fé, sem discutir assuntos controvertidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28283, 'nvi', 'Romanos', 'rm', 14, 2, 'Um crê que pode comer de tudo; já outro, cuja fé é fraca, come apenas alimentos vegetais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28284, 'nvi', 'Romanos', 'rm', 14, 3, 'Aquele que come de tudo não deve desprezar o que não come, e aquele que não come de tudo não deve condenar aquele que come, pois Deus o aceitou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28285, 'nvi', 'Romanos', 'rm', 14, 4, 'Quem é você para julgar o servo alheio? É para o seu senhor que ele está de pé ou cai. E ficará de pé, pois o Senhor é capaz de o sustentar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28286, 'nvi', 'Romanos', 'rm', 14, 5, 'Há quem considere um dia mais sagrado que outro; há quem considere iguais todos os dias. Cada um deve estar plenamente convicto em sua própria mente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28287, 'nvi', 'Romanos', 'rm', 14, 6, 'Aquele que considera um dia como especial, para o Senhor assim o faz. Aquele que come carne, come para o Senhor, pois dá graças a Deus; e aquele que se abstém, para o Senhor se abstém, e dá graças a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28288, 'nvi', 'Romanos', 'rm', 14, 7, 'Pois nenhum de nós vive apenas para si, e nenhum de nós morre apenas para si.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28289, 'nvi', 'Romanos', 'rm', 14, 8, 'Se vivemos, vivemos para o Senhor; e, se morremos, morremos para o Senhor. Assim, quer vivamos, quer morramos, pertencemos ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28290, 'nvi', 'Romanos', 'rm', 14, 9, 'Por esta razão Cristo morreu e voltou a viver, para ser Senhor de vivos e de mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28291, 'nvi', 'Romanos', 'rm', 14, 10, 'Portanto, você, por que julga seu irmão? E por que despreza seu irmão? Pois todos compareceremos diante do tribunal de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28292, 'nvi', 'Romanos', 'rm', 14, 11, 'Porque está escrito: " ‘Por mim mesmo jurei’, diz o Senhor, ‘diante de mim todo joelho se dobrará e toda língua confessará que sou Deus’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28293, 'nvi', 'Romanos', 'rm', 14, 12, 'Assim, cada um de nós prestará contas de si mesmo a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28294, 'nvi', 'Romanos', 'rm', 14, 13, 'Portanto, deixemos de julgar uns aos outros. Em vez disso, façamos o propósito de não colocar pedra de tropeço ou obstáculo no caminho do irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28295, 'nvi', 'Romanos', 'rm', 14, 14, 'Como alguém que está no Senhor Jesus, tenho plena convicção de que nenhum alimento é por si mesmo impuro, a não ser para quem assim o considere; para ele é impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28296, 'nvi', 'Romanos', 'rm', 14, 15, 'Se o seu irmão se entristece devido ao que você come, você já não está agindo por amor. Por causa da sua comida, não destrua seu irmão, por quem Cristo morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28297, 'nvi', 'Romanos', 'rm', 14, 16, 'Aquilo que é bom para vocês não se torne objeto de maledicência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28298, 'nvi', 'Romanos', 'rm', 14, 17, 'Pois o Reino de Deus não é comida nem bebida, mas justiça, paz e alegria no Espírito Santo;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28299, 'nvi', 'Romanos', 'rm', 14, 18, 'aquele que assim serve a Cristo é agradável a Deus e aprovado pelos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28300, 'nvi', 'Romanos', 'rm', 14, 19, 'Por isso, esforcemo-nos em promover tudo quanto conduz à paz e à edificação mútua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28301, 'nvi', 'Romanos', 'rm', 14, 20, 'Não destrua a obra de Deus por causa da comida. Todo alimento é puro, mas é errado comer qualquer coisa que faça os outros tropeçarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28302, 'nvi', 'Romanos', 'rm', 14, 21, 'É melhor não comer carne nem beber vinho, nem fazer qualquer outra coisa que leve seu irmão a cair.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28303, 'nvi', 'Romanos', 'rm', 14, 22, 'Assim, seja qual for o seu modo de crer a respeito destas coisas, que isso permaneça entre você e Deus. Feliz é o homem que não se condena naquilo que aprova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28304, 'nvi', 'Romanos', 'rm', 14, 23, 'Mas aquele que tem dúvida é condenado se comer, porque não come com fé; e tudo o que não provém da fé é pecado.');
