INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28305, 'nvi', 'Romanos', 'rm', 15, 1, 'Nós, que somos fortes, devemos suportar as fraquezas dos fracos, e não agradar a nós mesmos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28306, 'nvi', 'Romanos', 'rm', 15, 2, 'Cada um de nós deve agradar ao seu próximo para o bem dele, a fim de edificá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28307, 'nvi', 'Romanos', 'rm', 15, 3, 'Pois também Cristo não agradou a si próprio, mas, como está escrito: "Os insultos daqueles que te insultam caíram sobre mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28308, 'nvi', 'Romanos', 'rm', 15, 4, 'Pois tudo o que foi escrito no passado, foi escrito para nos ensinar, de forma que, por meio da perseverança e do bom ânimo procedentes das Escrituras, mantenhamos a nossa esperança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28309, 'nvi', 'Romanos', 'rm', 15, 5, 'O Deus que concede perseverança e ânimo dê-lhes um espírito de unidade, segundo Cristo Jesus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28310, 'nvi', 'Romanos', 'rm', 15, 6, 'para que com um só coração e uma só boca vocês glorifiquem ao Deus e Pai de nosso Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28311, 'nvi', 'Romanos', 'rm', 15, 7, 'Portanto, aceitem-se uns aos outros, da mesma forma como Cristo os aceitou, a fim de que vocês glorifiquem a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28312, 'nvi', 'Romanos', 'rm', 15, 8, 'Pois eu lhes digo que Cristo se tornou servo dos que são da circuncisão, por amor à verdade de Deus, para confirmar as promessas feitas aos patriarcas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28313, 'nvi', 'Romanos', 'rm', 15, 9, 'a fim de que os gentios glorifiquem a Deus por sua misericórdia, como está escrito: "Por isso, eu te louvarei entre os gentios; Cantarei louvores ao teu nome".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28314, 'nvi', 'Romanos', 'rm', 15, 10, 'E também diz: "Cantem de alegria, ó gentios, com o povo dele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28315, 'nvi', 'Romanos', 'rm', 15, 11, 'E mais: "Louvem o Senhor, todos vocês, gentios; cantem louvores a ele todos os povos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28316, 'nvi', 'Romanos', 'rm', 15, 12, 'E Isaías também diz: "Brotará a raiz de Jessé, aquele que se levantará para reinar sobre os gentios; estes colocarão nele a sua esperança".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28317, 'nvi', 'Romanos', 'rm', 15, 13, 'Que o Deus da esperança os encha de toda alegria e paz, por sua confiança nele, para que vocês transbordem de esperança, pelo poder do Espírito Santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28318, 'nvi', 'Romanos', 'rm', 15, 14, 'Meus irmãos, eu mesmo estou convencido de que vocês estão cheios de bondade e plenamente instruídos, sendo capazes de aconselhar-se uns aos outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28319, 'nvi', 'Romanos', 'rm', 15, 15, 'A respeito de alguns assuntos, eu lhes escrevi com toda a franqueza, como para fazê-los lembrar-se novamente deles, por causa da graça que Deus me deu,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28320, 'nvi', 'Romanos', 'rm', 15, 16, 'de ser um ministro de Cristo Jesus para os gentios, com o dever sacerdotal de proclamar o evangelho de Deus, para que os gentios se tornem uma oferta aceitável a Deus, santificados pelo Espírito Santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28321, 'nvi', 'Romanos', 'rm', 15, 17, 'Portanto, eu me glorio em Cristo Jesus, em meu serviço a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28322, 'nvi', 'Romanos', 'rm', 15, 18, 'Não me atrevo a falar de nada, exceto daquilo que Cristo realizou por meu intermédio em palavra e em ação, a fim de levar os gentios a obedecerem a Deus:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28323, 'nvi', 'Romanos', 'rm', 15, 19, 'pelo poder de sinais e maravilhas e por meio do poder do Espírito de Deus. Assim, desde Jerusalém e arredores, até o Ilírico, proclamei plenamente o evangelho de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28324, 'nvi', 'Romanos', 'rm', 15, 20, 'Sempre fiz questão de pregar o evangelho onde Cristo ainda não era conhecido, de forma que não estivesse edificando sobre alicerce de outro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28325, 'nvi', 'Romanos', 'rm', 15, 21, 'Mas antes, como está escrito: "Hão de vê-lo aqueles que não tinham ouvido falar dele, e o entenderão aqueles que não o haviam escutado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28326, 'nvi', 'Romanos', 'rm', 15, 22, 'É por isso que muitas vezes fui impedido de chegar até vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28327, 'nvi', 'Romanos', 'rm', 15, 23, 'Mas agora, não havendo nestas regiões nenhum lugar em que precise trabalhar, e visto que há muitos anos anseio vê-los,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28328, 'nvi', 'Romanos', 'rm', 15, 24, 'planejo fazê-lo quando for à Espanha. Espero visitá-los de passagem e dar-lhes a oportunidade de me ajudar em minha viagem para lá, depois de ter desfrutado um pouco da companhia de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28329, 'nvi', 'Romanos', 'rm', 15, 25, 'Agora, porém, estou de partida para Jerusalém, a serviço dos santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28330, 'nvi', 'Romanos', 'rm', 15, 26, 'Pois a Macedônia e a Acaia tiveram a alegria de contribuir para os pobres dentre os santos de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28331, 'nvi', 'Romanos', 'rm', 15, 27, 'Eles tiveram prazer nisso, e de fato são devedores a eles. Pois se os gentios participaram das bênçãos espirituais dos judeus, devem também servir aos judeus com seus bens materiais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28332, 'nvi', 'Romanos', 'rm', 15, 28, 'Assim, depois de completar essa tarefa e de ter a certeza de que eles receberam esse fruto, irei à Espanha e visitarei vocês de passagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28333, 'nvi', 'Romanos', 'rm', 15, 29, 'Sei que, quando for visitá-los, irei na plenitude da bênção de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28334, 'nvi', 'Romanos', 'rm', 15, 30, 'Recomendo-lhes, irmãos, por nosso Senhor Jesus Cristo e pelo amor do Espírito, que se unam a mim em minha luta, orando a Deus em meu favor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28335, 'nvi', 'Romanos', 'rm', 15, 31, 'Orem para que eu esteja livre dos descrentes da Judéia e que o meu serviço em Jerusalém seja aceitável aos santos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28336, 'nvi', 'Romanos', 'rm', 15, 32, 'de forma que, pela vontade de Deus, eu os visite com alegria e juntamente com vocês desfrute de um período de refrigério.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28337, 'nvi', 'Romanos', 'rm', 15, 33, 'O Deus da paz seja com todos vocês. Amém.');
