INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28338, 'nvi', 'Romanos', 'rm', 16, 1, 'Recomendo-lhes nossa irmã Febe, serva da igreja em Cencréia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28339, 'nvi', 'Romanos', 'rm', 16, 2, 'Peço que a recebam no Senhor, de maneira digna dos santos, e lhe prestem a ajuda de que venha a necessitar; pois tem sido de grande auxílio para muita gente, inclusive para mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28340, 'nvi', 'Romanos', 'rm', 16, 3, 'Saúdem Priscila e Áqüila, meus colaboradores em Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28341, 'nvi', 'Romanos', 'rm', 16, 4, 'Arriscaram a vida por mim. Sou grato a eles; não apenas eu, mas todas as igrejas dos gentios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28342, 'nvi', 'Romanos', 'rm', 16, 5, 'Saúdem também a igreja que se reúne na casa deles. Saúdem meu amado irmão Epêneto, que foi o primeiro convertido a Cristo na província da Ásia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28343, 'nvi', 'Romanos', 'rm', 16, 6, 'Saúdem Maria, que trabalhou arduamente por vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28344, 'nvi', 'Romanos', 'rm', 16, 7, 'Saúdem Andrônico e Júnias, meus parentes que estiveram na prisão comigo. São notáveis entre os apóstolos, e estavam em Cristo antes de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28345, 'nvi', 'Romanos', 'rm', 16, 8, 'Saúdem Amplíato, meu amado irmão no Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28346, 'nvi', 'Romanos', 'rm', 16, 9, 'Saúdem Urbano, nosso cooperador em Cristo, e meu amado irmão Estáquis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28347, 'nvi', 'Romanos', 'rm', 16, 10, 'Saúdem Apeles, aprovado em Cristo. Saúdem os que pertencem à casa de Aristóbulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28348, 'nvi', 'Romanos', 'rm', 16, 11, 'Saúdem Herodião, meu parente. Saúdem os da casa de Narciso, que estão no Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28349, 'nvi', 'Romanos', 'rm', 16, 12, 'Saúdem Trifena e Trifosa, mulheres que trabalham arduamente no Senhor. Saúdem a amada Pérside, outra que trabalhou arduamente no Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28350, 'nvi', 'Romanos', 'rm', 16, 13, 'Saúdem Rufo, eleito no Senhor, e sua mãe, que tem sido mãe também para mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28351, 'nvi', 'Romanos', 'rm', 16, 14, 'Saúdem Asíncrito, Flegonte, Hermes, Pátrobas, Hermas e os irmãos que estão com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28352, 'nvi', 'Romanos', 'rm', 16, 15, 'Saúdem Filólogo, Júlia, Nereu e sua irmã, e também Olimpas e todos os santos que estão com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28353, 'nvi', 'Romanos', 'rm', 16, 16, 'Saúdem uns aos outros com beijo santo. Todas as igrejas de Cristo enviam-lhes saudações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28354, 'nvi', 'Romanos', 'rm', 16, 17, 'Recomendo-lhes, irmãos, que tomem cuidado com aqueles que causam divisões e colocam obstáculos ao ensino que vocês têm recebido. Afastem-se deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28355, 'nvi', 'Romanos', 'rm', 16, 18, 'Pois essas pessoas não estão servindo a Cristo, nosso Senhor, mas a seus próprios apetites. Mediante palavras suaves e bajulação, enganam os corações dos ingênuos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28356, 'nvi', 'Romanos', 'rm', 16, 19, 'Todos têm ouvido falar da obediência de vocês, por isso estou muito alegre; mas quero que sejam sábios em relação ao que é bom, e sem malícia em relação ao que é mau.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28357, 'nvi', 'Romanos', 'rm', 16, 20, 'Em breve o Deus da paz esmagará Satanás debaixo dos pés de vocês. A graça de nosso Senhor Jesus seja com vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28358, 'nvi', 'Romanos', 'rm', 16, 21, 'Timóteo, meu cooperador, envia-lhes saudações, bem como Lúcio, Jasom e Sosípatro, meus parentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28359, 'nvi', 'Romanos', 'rm', 16, 22, 'Eu, Tércio, que redigi esta carta, saúdo vocês no Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28360, 'nvi', 'Romanos', 'rm', 16, 23, 'Gaio, cuja hospitalidade eu e toda a igreja desfrutamos, envia-lhes saudações. Erasto, administrador da cidade, e nosso irmão Quarto enviam-lhes saudações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28361, 'nvi', 'Romanos', 'rm', 16, 24, 'Que a graça de nosso Senhor Jesus Cristo seja com vocês todos. Amém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28362, 'nvi', 'Romanos', 'rm', 16, 25, 'Ora, àquele que tem poder para confirmá-los pelo meu evangelho e pela proclamação de Jesus Cristo, de acordo com a revelação do mistério oculto nos tempos passados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28363, 'nvi', 'Romanos', 'rm', 16, 26, 'mas agora revelado e dado a conhecer pelas Escrituras proféticas por ordem do Deus eterno, para que todas as nações venham a crer nele e a obedecer-lhe,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28364, 'nvi', 'Romanos', 'rm', 16, 27, 'ao único Deus sábio seja dada glória para todo o sempre, por meio de Jesus Cristo. Amém.');
