INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27964, 'nvi', 'Romanos', 'rm', 2, 1, 'Portanto, você, que julga, os outros é indesculpável; pois está condenando a si mesmo naquilo em que julga, visto que você, que julga, pratica as mesmas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27965, 'nvi', 'Romanos', 'rm', 2, 2, 'Sabemos que o juízo de Deus contra os que praticam tais coisas é conforme a verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27966, 'nvi', 'Romanos', 'rm', 2, 3, 'Assim, quando você, um simples homem, os julga, mas pratica as mesmas coisas, pensa que escapará do juízo de Deus?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27967, 'nvi', 'Romanos', 'rm', 2, 4, 'Ou será que você despreza as riquezas da sua bondade, tolerância e paciência, não reconhecendo que a bondade de Deus o leva ao arrependimento?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27968, 'nvi', 'Romanos', 'rm', 2, 5, 'Contudo, por causa da sua teimosia e do seu coração obstinado, você está acumulando ira contra si mesmo, para o dia da ira de Deus, quando se revelará o seu justo julgamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27969, 'nvi', 'Romanos', 'rm', 2, 6, 'Deus "retribuirá a cada um conforme o seu procedimento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27970, 'nvi', 'Romanos', 'rm', 2, 7, 'Ele dará vida eterna aos que, persistindo em fazer o bem, buscam glória, honra e imortalidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27971, 'nvi', 'Romanos', 'rm', 2, 8, 'Mas haverá ira e indignação para os que são egoístas, que rejeitam a verdade e seguem a injustiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27972, 'nvi', 'Romanos', 'rm', 2, 9, 'Haverá tribulação e angústia para todo ser humano que pratica o mal: primeiro para o judeu, depois para o grego;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27973, 'nvi', 'Romanos', 'rm', 2, 10, 'mas glória, honra e paz para todo o que pratica o bem: primeiro para o judeu, depois para o grego.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27974, 'nvi', 'Romanos', 'rm', 2, 11, 'Pois em Deus não há parcialidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27975, 'nvi', 'Romanos', 'rm', 2, 12, 'Todo aquele que pecar sem a lei, sem a lei também perecerá, e todo aquele que pecar sob a lei, pela lei será julgado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27976, 'nvi', 'Romanos', 'rm', 2, 13, 'Porque não são os que ouvem a Lei que são justos aos olhos de Deus; mas os que obedecem à lei, estes serão declarados justos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27977, 'nvi', 'Romanos', 'rm', 2, 14, '( De fato, quando os gentios, que não têm a lei, praticam naturalmente o que ela ordena, tornam-se lei para si mesmos, embora não possuam a lei;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27978, 'nvi', 'Romanos', 'rm', 2, 15, 'pois mostram que as exigências da lei estão gravadas em seus corações. Disso dão testemunho também a consciência e os pensamentos deles, ora acusando-os, ora defendendo-os. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27979, 'nvi', 'Romanos', 'rm', 2, 16, 'Isso acontecerá no dia em que Deus julgar os segredos dos homens, mediante Jesus Cristo, conforme o declara o meu evangelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27980, 'nvi', 'Romanos', 'rm', 2, 17, 'Ora, você que leva o nome de judeu, apóia-se na lei e orgulha-se em Deus;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27981, 'nvi', 'Romanos', 'rm', 2, 18, 'se você conhece a vontade de Deus e aprova o que é superior, porque é instruído pela lei;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27982, 'nvi', 'Romanos', 'rm', 2, 19, 'se está convencido de que é guia de cegos, luz para os que estão em trevas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27983, 'nvi', 'Romanos', 'rm', 2, 20, 'instrutor de insensatos, mestre de crianças, porque tem na lei a expressão do conhecimento e da verdade;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27984, 'nvi', 'Romanos', 'rm', 2, 21, 'então você, que ensina os outros, não ensina a si mesmo? Você, que prega contra o furto, furta?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27985, 'nvi', 'Romanos', 'rm', 2, 22, 'Você, que diz que não se deve adulterar, adultera? Você, que detesta ídolos, rouba-lhes os templos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27986, 'nvi', 'Romanos', 'rm', 2, 23, 'Você, que se orgulha na lei, desonra a Deus, desobedecendo à lei?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27987, 'nvi', 'Romanos', 'rm', 2, 24, 'Como está escrito: "O nome de Deus é blasfemado entre os gentios por causa de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27988, 'nvi', 'Romanos', 'rm', 2, 25, 'A circuncisão tem valor se você obedece à lei; mas, se você desobedece à lei, a sua circuncisão já se tornou incircuncisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27989, 'nvi', 'Romanos', 'rm', 2, 26, 'Se aqueles que não são circuncidados obedecem aos preceitos da lei, não serão eles considerados circuncidados?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27990, 'nvi', 'Romanos', 'rm', 2, 27, 'Aquele que não é circuncidado fisicamente, mas obedece à lei, condenará você que, tendo a lei escrita e a circuncisão, é transgressor da lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27991, 'nvi', 'Romanos', 'rm', 2, 28, 'Não é judeu quem o é apenas exteriormente, nem é circuncisão a que é meramente exterior e física.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27992, 'nvi', 'Romanos', 'rm', 2, 29, 'Não! Judeu é quem o é interiormente, e circuncisão é a operada no coração, pelo Espírito, e não pela lei escrita. Para estes o louvor não provém dos homens, mas de Deus.');
