INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27993, 'nvi', 'Romanos', 'rm', 3, 1, 'Que vantagem há então em ser judeu, ou que utilidade há na circuncisão?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27994, 'nvi', 'Romanos', 'rm', 3, 2, 'Muita, em todos os sentidos! Principalmente porque aos judeus foram confiadas as palavras de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27995, 'nvi', 'Romanos', 'rm', 3, 3, 'Que importa se alguns deles foram infiéis? A sua infidelidade anulará a fidelidade de Deus?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27996, 'nvi', 'Romanos', 'rm', 3, 4, 'De maneira nenhuma! Seja Deus verdadeiro, e todo homem mentiroso. Como está escrito: "De modo que são justas as tuas palavras e prevaleces quando julgas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27997, 'nvi', 'Romanos', 'rm', 3, 5, 'Mas, se a nossa injustiça ressalta de maneira ainda mais clara a justiça de Deus, que diremos? Que Deus é injusto por aplicar a sua ira? ( Estou usando um argumento humano. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27998, 'nvi', 'Romanos', 'rm', 3, 6, 'Claro que não! Se fosse assim, como Deus iria julgar o mundo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27999, 'nvi', 'Romanos', 'rm', 3, 7, 'Alguém pode alegar ainda: "Se a minha mentira ressalta a veracidade de Deus, aumentando assim a sua glória, por que sou condenado como pecador? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28000, 'nvi', 'Romanos', 'rm', 3, 8, 'Por que não dizer como alguns caluniosamente afirmam que dizemos: "Façamos o mal, para que nos venha o bem"? A condenação dos tais é merecida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28001, 'nvi', 'Romanos', 'rm', 3, 9, 'Que concluiremos então? Estamos em posição de vantagem? Não! Já demonstramos que tanto judeus quanto gentios estão debaixo do pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28002, 'nvi', 'Romanos', 'rm', 3, 10, 'Como está escrito: "Não há nenhum justo, nem um sequer;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28003, 'nvi', 'Romanos', 'rm', 3, 11, 'não há ninguém que entenda, ninguém que busque a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28004, 'nvi', 'Romanos', 'rm', 3, 12, 'Todos se desviaram, tornaram-se juntamente inúteis; não há ninguém que faça o bem, não há nem um sequer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28005, 'nvi', 'Romanos', 'rm', 3, 13, '"Suas gargantas são um túmulo aberto; com suas línguas enganam". "Veneno de serpentes está em seus lábios".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28006, 'nvi', 'Romanos', 'rm', 3, 14, '"Suas bocas estão cheias de maldição e amargura".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28007, 'nvi', 'Romanos', 'rm', 3, 15, '"Seus pés são ágeis para derramar sangue;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28008, 'nvi', 'Romanos', 'rm', 3, 16, 'ruína e desgraça marcam os seus caminhos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28009, 'nvi', 'Romanos', 'rm', 3, 17, 'e não conhecem o caminho da paz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28010, 'nvi', 'Romanos', 'rm', 3, 18, '"Aos seus olhos é inútil temer a Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28011, 'nvi', 'Romanos', 'rm', 3, 19, 'Sabemos que tudo o que a lei diz, o diz àqueles que estão debaixo dela, para que toda boca se cale e todo o mundo esteja sob o juízo de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28012, 'nvi', 'Romanos', 'rm', 3, 20, 'Portanto, ninguém será declarado justo diante dele baseando-se na obediência à lei, pois é mediante a lei que nos tornamos plenamente conscientes do pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28013, 'nvi', 'Romanos', 'rm', 3, 21, 'Mas agora se manifestou uma justiça que provém de Deus, independente da lei, da qual testemunham a Lei e os Profetas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28014, 'nvi', 'Romanos', 'rm', 3, 22, 'justiça de Deus mediante a fé em Jesus Cristo para todos os que crêem. Não há distinção,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28015, 'nvi', 'Romanos', 'rm', 3, 23, 'pois todos pecaram e estão destituídos da glória de Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28016, 'nvi', 'Romanos', 'rm', 3, 24, 'sendo justificados gratuitamente por sua graça, por meio da redenção que há em Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28017, 'nvi', 'Romanos', 'rm', 3, 25, 'Deus o ofereceu como sacrifício para propiciação mediante a fé, pelo seu sangue, demonstrando a sua justiça. Em sua tolerância, havia deixado impunes os pecados anteriormente cometidos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28018, 'nvi', 'Romanos', 'rm', 3, 26, 'mas, no presente, demonstrou a sua justiça, a fim de ser justo e justificador daquele que tem fé em Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28019, 'nvi', 'Romanos', 'rm', 3, 27, 'Onde está, então, o motivo de vanglória? É excluído. Baseado em que princípio? No da obediência à lei? Não, mas no princípio da fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28020, 'nvi', 'Romanos', 'rm', 3, 28, 'Pois sustentamos que o homem é justificado pela fé, independente da obediência à lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28021, 'nvi', 'Romanos', 'rm', 3, 29, 'Deus é Deus apenas dos judeus? Ele não é também o Deus dos gentios? Sim, dos gentios também,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28022, 'nvi', 'Romanos', 'rm', 3, 30, 'visto que existe um só Deus, que pela fé justificará os circuncisos e os incircuncisos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28023, 'nvi', 'Romanos', 'rm', 3, 31, 'Anulamos então a lei pela fé? De maneira nenhuma! Pelo contrário, confirmamos a lei.');
