INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28049, 'nvi', 'Romanos', 'rm', 5, 1, 'Tendo sido, pois, justificados pela fé, temos paz com Deus, por nosso Senhor Jesus Cristo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28050, 'nvi', 'Romanos', 'rm', 5, 2, 'por meio de quem obtivemos acesso pela fé a esta graça na qual agora estamos firmes; e nos gloriamos na esperança da glória de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28051, 'nvi', 'Romanos', 'rm', 5, 3, 'Não só isso, mas também nos gloriamos nas tribulações, porque sabemos que a tribulação produz perseverança;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28052, 'nvi', 'Romanos', 'rm', 5, 4, 'a perseverança, um caráter aprovado; e o caráter aprovado, esperança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28053, 'nvi', 'Romanos', 'rm', 5, 5, 'E a esperança não nos decepciona, porque Deus derramou seu amor em nossos corações, por meio do Espírito Santo que ele nos concedeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28054, 'nvi', 'Romanos', 'rm', 5, 6, 'De fato, no devido tempo, quando ainda éramos fracos, Cristo morreu pelos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28055, 'nvi', 'Romanos', 'rm', 5, 7, 'Dificilmente haverá alguém que morra por um justo; pelo homem bom talvez alguém tenha coragem de morrer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28056, 'nvi', 'Romanos', 'rm', 5, 8, 'Mas Deus demonstra seu amor por nós: Cristo morreu em nosso favor quando ainda éramos pecadores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28057, 'nvi', 'Romanos', 'rm', 5, 9, 'Como agora fomos justificados por seu sangue, muito mais ainda seremos salvos da ira de Deus por meio dele!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28058, 'nvi', 'Romanos', 'rm', 5, 10, 'Se quando éramos inimigos de Deus fomos reconciliados com ele mediante a morte de seu Filho, quanto mais agora, tendo sido reconciliados, seremos salvos por sua vida!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28059, 'nvi', 'Romanos', 'rm', 5, 11, 'Não apenas isso, mas também nos gloriamos em Deus, por meio de nosso Senhor Jesus Cristo, mediante quem recebemos agora a reconciliação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28060, 'nvi', 'Romanos', 'rm', 5, 12, 'Portanto, da mesma forma como o pecado entrou no mundo por um homem, e pelo pecado a morte, assim também a morte veio a todos os homens, porque todos pecaram;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28061, 'nvi', 'Romanos', 'rm', 5, 13, 'pois antes de ser dada a lei, o pecado já estava no mundo. Mas o pecado não é levado em conta quando não existe lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28062, 'nvi', 'Romanos', 'rm', 5, 14, 'Todavia, a morte reinou desde o tempo de Adão até o de Moisés, mesmo sobre aqueles que não cometeram pecado semelhante à transgressão de Adão, o qual era um tipo daquele que haveria de vir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28063, 'nvi', 'Romanos', 'rm', 5, 15, 'Entretanto, não há comparação entre a dádiva e a transgressão. Pois se muitos morreram por causa da transgressão de um só, muito mais a graça de Deus, isto é, a dádiva pela graça de um só homem, Jesus Cristo, transbordou para muitos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28064, 'nvi', 'Romanos', 'rm', 5, 16, 'Não se pode comparar a dádiva de Deus com a conseqüência do pecado de um só homem: por um pecado veio o julgamento que trouxe condenação, mas a dádiva decorreu de muitas transgressões e trouxe justificação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28065, 'nvi', 'Romanos', 'rm', 5, 17, 'Se pela transgressão de um só a morte reinou por meio dele, muito mais aqueles que recebem de Deus a imensa provisão da graça e a dádiva da justiça reinarão em vida por meio de um único homem, Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28066, 'nvi', 'Romanos', 'rm', 5, 18, 'Conseqüentemente, assim como uma só transgressão resultou na condenação de todos os homens, assim também um só ato de justiça resultou na justificação que traz vida a todos os homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28067, 'nvi', 'Romanos', 'rm', 5, 19, 'Logo, assim como por meio da desobediência de um só homem muitos foram feitos pecadores, assim também, por meio da obediência de um único homem muitos serão feitos justos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28068, 'nvi', 'Romanos', 'rm', 5, 20, 'A lei foi introduzida para que a transgressão fosse ressaltada. Mas onde aumentou o pecado, transbordou a graça,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28069, 'nvi', 'Romanos', 'rm', 5, 21, 'a fim de que, assim como o pecado reinou na morte, também a graça reine pela justiça para conceder vida eterna, mediante Jesus Cristo, nosso Senhor.');
