INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28070, 'nvi', 'Romanos', 'rm', 6, 1, 'Que diremos então? Continuaremos pecando para que a graça aumente?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28071, 'nvi', 'Romanos', 'rm', 6, 2, 'De maneira nenhuma! Nós, os que morremos para o pecado, como podemos continuar vivendo nele?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28072, 'nvi', 'Romanos', 'rm', 6, 3, 'Ou vocês não sabem que todos nós, que fomos batizados em Cristo Jesus, fomos batizados em sua morte?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28073, 'nvi', 'Romanos', 'rm', 6, 4, 'Portanto, fomos sepultados com ele na morte por meio do batismo, a fim de que, assim como Cristo foi ressuscitado dos mortos mediante a glória do Pai, também nós vivamos uma vida nova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28074, 'nvi', 'Romanos', 'rm', 6, 5, 'Se dessa forma fomos unidos a ele na semelhança da sua morte, certamente o seremos também na semelhança da sua ressurreição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28075, 'nvi', 'Romanos', 'rm', 6, 6, 'Pois sabemos que o nosso velho homem foi crucificado com ele, para que o corpo do pecado seja destruído, e não mais sejamos escravos do pecado;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28076, 'nvi', 'Romanos', 'rm', 6, 7, 'pois quem morreu, foi justificado do pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28077, 'nvi', 'Romanos', 'rm', 6, 8, 'Ora, se morremos com Cristo, cremos que também com ele viveremos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28078, 'nvi', 'Romanos', 'rm', 6, 9, 'Pois sabemos que, tendo sido ressuscitado dos mortos, Cristo não pode morrer outra vez: a morte não tem mais domínio sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28079, 'nvi', 'Romanos', 'rm', 6, 10, 'Porque morrendo, ele morreu para o pecado uma vez por todas; mas vivendo, vive para Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28080, 'nvi', 'Romanos', 'rm', 6, 11, 'Da mesma forma, considerem-se mortos para o pecado, mas vivos para Deus em Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28081, 'nvi', 'Romanos', 'rm', 6, 12, 'Portanto, não permitam que o pecado continue dominando os seus corpos mortais, fazendo que vocês obedeçam aos seus desejos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28082, 'nvi', 'Romanos', 'rm', 6, 13, 'Não ofereçam os membros dos seus corpos ao pecado, como instrumentos de injustiça; antes ofereçam-se a Deus como quem voltou da morte para a vida; e ofereçam os membros dos seus corpos a ele, como instrumentos de justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28083, 'nvi', 'Romanos', 'rm', 6, 14, 'Pois o pecado não os dominará, porque vocês não estão debaixo da lei, mas debaixo da graça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28084, 'nvi', 'Romanos', 'rm', 6, 15, 'E então? Vamos pecar porque não estamos debaixo da lei, mas debaixo da graça? De maneira nenhuma!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28085, 'nvi', 'Romanos', 'rm', 6, 16, 'Não sabem que, quando vocês se oferecem a alguém para lhe obedecer como escravos, tornam-se escravos daquele a quem obedecem: escravos do pecado que leva à morte, ou da obediência que leva à justiça?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28086, 'nvi', 'Romanos', 'rm', 6, 17, 'Mas, graças a Deus, porque, embora vocês tenham sido escravos do pecado, passaram a obedecer de coração à forma de ensino que lhes foi transmitida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28087, 'nvi', 'Romanos', 'rm', 6, 18, 'Vocês foram libertados do pecado e tornaram-se escravos da justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28088, 'nvi', 'Romanos', 'rm', 6, 19, 'Falo isso em termos humanos por causa das suas limitações humanas. Assim como vocês ofereceram os membros dos seus corpos em escravidão à impureza e à maldade que leva à maldade, ofereçam-nos agora em escravidão à justiça que leva à santidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28089, 'nvi', 'Romanos', 'rm', 6, 20, 'Quando vocês eram escravos do pecado, estavam livres da justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28090, 'nvi', 'Romanos', 'rm', 6, 21, 'Que fruto colheram então das coisas das quais agora vocês se envergonham? O fim delas é a morte!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28091, 'nvi', 'Romanos', 'rm', 6, 22, 'Mas agora que vocês foram libertados do pecado e se tornaram escravos de Deus, o fruto que colhem leva à santidade, e o seu fim é a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28092, 'nvi', 'Romanos', 'rm', 6, 23, 'Pois o salário do pecado é a morte, mas o dom gratuito de Deus é a vida eterna em Cristo Jesus, nosso Senhor.');
