INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28118, 'nvi', 'Romanos', 'rm', 8, 1, 'Portanto, agora já não há condenação para os que estão em Cristo Jesus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28119, 'nvi', 'Romanos', 'rm', 8, 2, 'porque por meio de Cristo Jesus a lei do Espírito de vida me libertou da lei do pecado e da morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28120, 'nvi', 'Romanos', 'rm', 8, 3, 'Porque, aquilo que a lei fora incapaz de fazer por estar enfraquecida pela carne, Deus o fez, enviando seu próprio Filho, à semelhança do homem pecador, como oferta pelo pecado. E assim condenou o pecado na carne,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28121, 'nvi', 'Romanos', 'rm', 8, 4, 'a fim de que as justas exigências da lei fossem plenamente satisfeitas em nós, que não vivemos segundo a carne, mas segundo o Espírito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28122, 'nvi', 'Romanos', 'rm', 8, 5, 'Quem vive segundo a carne tem a mente voltada para o que a carne deseja; mas quem, de acordo com o Espírito, tem a mente voltada para o que o Espírito deseja.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28123, 'nvi', 'Romanos', 'rm', 8, 6, 'A mentalidade da carne é morte, mas a mentalidade do Espírito é vida e paz;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28124, 'nvi', 'Romanos', 'rm', 8, 7, 'a mentalidade da carne é inimiga de Deus porque não se submete à lei de Deus, nem pode fazê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28125, 'nvi', 'Romanos', 'rm', 8, 8, 'Quem é dominado pela carne não pode agradar a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28126, 'nvi', 'Romanos', 'rm', 8, 9, 'Entretanto, vocês não estão sob o domínio da carne, mas do Espírito, se de fato o Espírito de Deus habita em vocês. E, se alguém não tem o Espírito de Cristo, não pertence a Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28127, 'nvi', 'Romanos', 'rm', 8, 10, 'Mas se Cristo está em vocês, o corpo está morto por causa do pecado, mas o espírito está vivo por causa da justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28128, 'nvi', 'Romanos', 'rm', 8, 11, 'E, se o Espírito daquele que ressuscitou Jesus dentre os mortos habita em vocês, aquele que ressuscitou a Cristo dentre os mortos também dará vida a seus corpos mortais, por meio do seu Espírito, que habita em vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28129, 'nvi', 'Romanos', 'rm', 8, 12, 'Portanto, irmãos, estamos em dívida, não para com a carne, para vivermos sujeitos a ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28130, 'nvi', 'Romanos', 'rm', 8, 13, 'Pois se vocês viverem de acordo com a carne, morrerão; mas, se pelo Espírito fizerem morrer os atos do corpo, viverão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28131, 'nvi', 'Romanos', 'rm', 8, 14, 'porque todos os que são guiados pelo Espírito de Deus são filhos de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28132, 'nvi', 'Romanos', 'rm', 8, 15, 'Pois vocês não receberam um espírito que os escravize para novamente temer, mas receberam o Espírito que os adota como filhos, por meio do qual clamamos: "Aba, Pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28133, 'nvi', 'Romanos', 'rm', 8, 16, 'O próprio Espírito testemunha ao nosso espírito que somos filhos de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28134, 'nvi', 'Romanos', 'rm', 8, 17, 'Se somos filhos, então somos herdeiros; herdeiros de Deus e co-herdeiros com Cristo, se de fato participamos dos seus sofrimentos, para que também participemos da sua glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28135, 'nvi', 'Romanos', 'rm', 8, 18, 'Considero que os nossos sofrimentos atuais não podem ser comparados com a glória que em nós será revelada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28136, 'nvi', 'Romanos', 'rm', 8, 19, 'A natureza criada aguarda, com grande expectativa, que os filhos de Deus sejam revelados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28137, 'nvi', 'Romanos', 'rm', 8, 20, 'Pois ela foi submetida à futilidade, não pela sua própria escolha, mas por causa da vontade daquele que a sujeitou, na esperança');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28138, 'nvi', 'Romanos', 'rm', 8, 21, 'de que a própria natureza criada será libertada da escravidão da decadência em que se encontra para a gloriosa liberdade dos filhos de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28139, 'nvi', 'Romanos', 'rm', 8, 22, 'Sabemos que toda a natureza criada geme até agora, como em dores de parto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28140, 'nvi', 'Romanos', 'rm', 8, 23, 'E não só isso, mas nós mesmos, que temos os primeiros frutos do Espírito, gememos interiormente, esperando ansiosamente nossa adoção como filhos, a redenção do nosso corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28141, 'nvi', 'Romanos', 'rm', 8, 24, 'Pois nessa esperança fomos salvos. Mas, esperança que se vê não é esperança. Quem espera por aquilo que está vendo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28142, 'nvi', 'Romanos', 'rm', 8, 25, 'Mas se esperamos o que ainda não vemos, aguardamo-lo pacientemente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28143, 'nvi', 'Romanos', 'rm', 8, 26, 'Da mesma forma o Espírito nos ajuda em nossa fraqueza, pois não sabemos como orar, mas o próprio Espírito intercede por nós com gemidos inexprimíveis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28144, 'nvi', 'Romanos', 'rm', 8, 27, 'E aquele que sonda os corações conhece a intenção do Espírito, porque o Espírito intercede pelos santos de acordo com a vontade de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28145, 'nvi', 'Romanos', 'rm', 8, 28, 'Sabemos que Deus age em todas as coisas para o bem daqueles que o amam, dos que foram chamados de acordo com o seu propósito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28146, 'nvi', 'Romanos', 'rm', 8, 29, 'Pois aqueles que de antemão conheceu, também os predestinou para serem conformes à imagem de seu Filho, a fim de que ele seja o primogênito entre muitos irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28147, 'nvi', 'Romanos', 'rm', 8, 30, 'E aos que predestinou, também chamou; aos que chamou, também justificou; aos que justificou, também glorificou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28148, 'nvi', 'Romanos', 'rm', 8, 31, 'Que diremos, pois, diante dessas coisas? Se Deus é por nós, quem será contra nós?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28149, 'nvi', 'Romanos', 'rm', 8, 32, 'Aquele que não poupou a seu próprio Filho, mas o entregou por todos nós, como não nos dará juntamente com ele, e de graça, todas as coisas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28150, 'nvi', 'Romanos', 'rm', 8, 33, 'Quem fará alguma acusação contra os escolhidos de Deus? É Deus quem os justifica.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28151, 'nvi', 'Romanos', 'rm', 8, 34, 'Quem os condenará? Foi Cristo Jesus que morreu; e mais, que ressuscitou e está à direita de Deus, e também intercede por nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28152, 'nvi', 'Romanos', 'rm', 8, 35, 'Quem nos separará do amor de Cristo? Será tribulação, ou angústia, ou perseguição, ou fome, ou nudez, ou perigo, ou espada?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28153, 'nvi', 'Romanos', 'rm', 8, 36, 'Como está escrito: "Por amor de ti enfrentamos a morte todos os dias; somos considerados como ovelhas destinadas ao matadouro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28154, 'nvi', 'Romanos', 'rm', 8, 37, 'Mas, em todas estas coisas somos mais que vencedores, por meio daquele que nos amou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28155, 'nvi', 'Romanos', 'rm', 8, 38, 'Pois estou convencido de que nem morte nem vida, nem anjos nem demônios, nem o presente nem o futuro, nem quaisquer poderes,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28156, 'nvi', 'Romanos', 'rm', 8, 39, 'nem altura nem profundidade, nem qualquer outra coisa na criação será capaz de nos separar do amor de Deus que está em Cristo Jesus, nosso Senhor.');
