INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7128, 'nvi', 'Rute', 'rt', 1, 1, 'Na época dos juízes houve fome na terra. Um homem de Belém de Judá, com a mulher e os dois filhos, foi viver por algum tempo nas terras de Moabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7129, 'nvi', 'Rute', 'rt', 1, 2, 'O homem chamava-se Elimeleque, sua mulher Noemi e seus dois filhos Malom e Quiliom. Eram efrateus de Belém de Judá. Chegaram a Moabe, e lá ficaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7130, 'nvi', 'Rute', 'rt', 1, 3, 'Morreu Elimeleque, marido de Noemi, e ela ficou sozinha, com seus dois filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7131, 'nvi', 'Rute', 'rt', 1, 4, 'Eles se casaram com mulheres moabitas, uma chamada Orfa e a outra Rute. Depois de terem morado lá por quase dez anos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7132, 'nvi', 'Rute', 'rt', 1, 5, 'morreram também Malom e Quiliom, e Noemi ficou sozinha, sem os dois filhos e o seu marido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7133, 'nvi', 'Rute', 'rt', 1, 6, 'Quando Noemi soube em Moabe que o Senhor viera em auxílio do seu povo, dando-lhe alimento, decidiu voltar com suas duas noras para a sua terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7134, 'nvi', 'Rute', 'rt', 1, 7, 'Assim ela, com as duas noras, partiu do lugar onde tinha morado. Enquanto voltavam para a terra de Judá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7135, 'nvi', 'Rute', 'rt', 1, 8, 'Noemi disse às duas noras: "Vão! Voltem para a casa de suas mães! Que o Senhor seja leal com vocês, como vocês foram leais com os falecidos e comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7136, 'nvi', 'Rute', 'rt', 1, 9, 'O Senhor conceda que cada uma de vocês encontre segurança no lar doutro marido". Então deu-lhes beijos de despedida. Mas elas começaram a chorar bem alto');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7137, 'nvi', 'Rute', 'rt', 1, 10, 'e lhe disseram: "Não! Voltaremos com você para junto de seu povo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7138, 'nvi', 'Rute', 'rt', 1, 11, 'Disse, porém, Noemi: "Voltem, minhas filhas! Por que viriam comigo? Poderia eu ainda ter filhos, que viessem a ser seus maridos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7139, 'nvi', 'Rute', 'rt', 1, 12, 'Voltem, minhas filhas! Vão! Estou velha demais para ter outro marido. E mesmo que eu pensasse que ainda há esperança para mim — ainda que eu me casasse esta noite e depois desse à luz filhos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7140, 'nvi', 'Rute', 'rt', 1, 13, 'iriam vocês esperar até que eles crescessem? Ficariam sem se casar à espera deles? De jeito nenhum minhas filhas! Para mim é mais amargo do que para vocês, pois a mão do Senhor voltou-se contra mim! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7141, 'nvi', 'Rute', 'rt', 1, 14, 'Elas então começaram a choram bem alto de novo. Depois Orfa deu um beijo de despedida em sua sogra, mas Rute ficou com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7142, 'nvi', 'Rute', 'rt', 1, 15, 'Então Noemi a aconselhou: "Veja, sua concunhada está voltando para o seu povo e para o seu deus. Volte com ela! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7143, 'nvi', 'Rute', 'rt', 1, 16, 'Rute, porém, respondeu: "Não insistas comigo que te deixe e não mais a acompanhe. Aonde fores irei, onde ficares ficarei! O teu povo será o meu povo e o teu Deus será o meu Deus!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7144, 'nvi', 'Rute', 'rt', 1, 17, 'Onde morreres morrerei, e ali serei sepultada. Que o Senhor me castigue com todo o rigor, se outra coisa que não a morte me separar de ti! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7145, 'nvi', 'Rute', 'rt', 1, 18, 'Quando Noemi viu que Rute estava de fato decidida a acompanhá-la, não insistiu mais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7146, 'nvi', 'Rute', 'rt', 1, 19, 'Prosseguiram, pois, as duas até Belém. Ali chegando, todo o povoado ficou alvoroçado por causa delas. "Será que é Noemi? ", perguntavam as mulheres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7147, 'nvi', 'Rute', 'rt', 1, 20, 'Mas ela respondeu: "Não me chamem Noemi, chamem-me Mara, pois o Todo-poderoso tornou minha vida muito amarga!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7148, 'nvi', 'Rute', 'rt', 1, 21, 'De mãos cheias eu parti; mas de mãos vazias o Senhor me trouxe de volta. Por que me chamam Noemi? O Senhor colocou-se contra mim! O Todo-poderoso me trouxe desgraça! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7149, 'nvi', 'Rute', 'rt', 1, 22, 'Foi assim que Noemi voltou das terras de Moabe, com sua nora Rute, a moabita. Elas chegaram a Belém no início da colheita da cevada.');
