INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22822, 'nvi', 'Sofonias', 'sf', 3, 1, 'Ai da cidade rebelde, impura e opressora!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22823, 'nvi', 'Sofonias', 'sf', 3, 2, 'Não ouve a ninguém, e não aceita correção. Não confia no Senhor, não se aproxima do seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22824, 'nvi', 'Sofonias', 'sf', 3, 3, 'No meio dela os seus líderes são leões que rugem. Seus juízes são lobos vespertinos que nada deixam para a manhã seguinte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22825, 'nvi', 'Sofonias', 'sf', 3, 4, 'Seus profetas são irresponsáveis, são homens traiçoeiros. Seus sacerdotes profanam o santuário e fazem violência à lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22826, 'nvi', 'Sofonias', 'sf', 3, 5, 'No meio dela está o Senhor, que é justo e jamais comete injustiça. A cada manhã ele ministra a sua justiça, e a cada novo dia ele não falha, mas o injusto não se envergonha da sua injustiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22827, 'nvi', 'Sofonias', 'sf', 3, 6, '"Eliminei nações; suas fortificações estão devastadas. Deixei desertas as suas ruas. Suas cidades estão destruídas; ninguém foi deixado; ninguém!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22828, 'nvi', 'Sofonias', 'sf', 3, 7, 'Eu disse à cidade: Com certeza você me temerá e aceitará correção! Pois, então, a sua habitação não seria eliminada, nem cairiam sobre ela todos os meus castigos. Mas eles ainda estavam ávidos por fazer todo tipo de maldade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22829, 'nvi', 'Sofonias', 'sf', 3, 8, 'Por isso, esperem por mim, " declara o Senhor, "no dia em que eu me levantar para testemunhar. Decidi ajuntar as nações, reunir os reinos e derramar a minha ira sobre eles, toda a minha impetuosa indignação. O mundo inteiro será consumido pelo fogo da minha zelosa ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22830, 'nvi', 'Sofonias', 'sf', 3, 9, '"Então purificarei os lábios dos povos, para que todos eles invoquem o nome do Senhor e o sirvam de comum acordo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22831, 'nvi', 'Sofonias', 'sf', 3, 10, 'Desde além dos rios da Etiópia os meus adoradores, o meu povo disperso, me trará ofertas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22832, 'nvi', 'Sofonias', 'sf', 3, 11, 'Naquele dia vocês não serão envergonhados pelos seus atos de rebelião, porque retirarei desta cidade os que se regozijam em seu orgulho. Nunca mais vocês serão altivos no meu santo monte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22833, 'nvi', 'Sofonias', 'sf', 3, 12, 'Mas deixarei no meio da cidade os mansos e humildes, que se refugiarão no nome do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22834, 'nvi', 'Sofonias', 'sf', 3, 13, 'O remanescente de Israel não cometerá injustiças; eles não mentirão, nem se achará engano em suas bocas. Eles se alimentarão e descansarão, sem que ninguém os amedronte. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22835, 'nvi', 'Sofonias', 'sf', 3, 14, 'Cante, ó cidade de Sião; exulte, ó Israel! Alegre-se, regozije-se de todo o coração, ó cidade de Jerusalém!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22836, 'nvi', 'Sofonias', 'sf', 3, 15, 'O Senhor anulou a sentença contra você, ele fez retroceder os seus inimigos. O Senhor, o Rei de Israel, está em seu meio; nunca mais você temerá perigo algum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22837, 'nvi', 'Sofonias', 'sf', 3, 16, 'Naquele dia se dirá a Jerusalém: "Não tema, ó Sião; não deixe suas mãos enfraquecerem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22838, 'nvi', 'Sofonias', 'sf', 3, 17, 'O Senhor, o seu Deus, está em seu meio, poderoso para salvar. Ele se regozijará em você, com o seu amor a renovará, ele se regozijará em você com brados de alegria".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22839, 'nvi', 'Sofonias', 'sf', 3, 18, '"Eu ajuntarei os que choram pelas festas fixas, os que se afastaram de vocês, para que isso não mais pese como vergonha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22840, 'nvi', 'Sofonias', 'sf', 3, 19, 'Nessa época agirei contra todos os que oprimiram vocês; salvarei os aleijados e ajuntarei os dispersos. Darei a eles louvor e honra em todas as terras onde foram envergonhados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22841, 'nvi', 'Sofonias', 'sf', 3, 20, 'Naquele tempo eu ajuntarei vocês; naquele tempo os trarei para casa. Eu lhes darei honra e louvor entre todos os povos da terra, quando eu restaurar a sua sorte diante dos seus próprios olhos", diz o Senhor.');
