INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13941, 'nvi', 'Salmos', 'sl', 1, 1, 'Como é feliz aquele que não segue o conselho dos ímpios, não imita a conduta dos pecadores, nem se assenta na roda dos zombadores!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13942, 'nvi', 'Salmos', 'sl', 1, 2, 'Ao contrário, sua satisfação está na lei do Senhor, e nessa lei medita dia e noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13943, 'nvi', 'Salmos', 'sl', 1, 3, 'É como árvore plantada à beira de águas correntes: Dá fruto no tempo certo e suas folhas não murcham. Tudo o que ele faz prospera!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13944, 'nvi', 'Salmos', 'sl', 1, 4, 'Não é o caso dos ímpios! São como palha que o vento leva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13945, 'nvi', 'Salmos', 'sl', 1, 5, 'Por isso os ímpios não resistirão no julgamento, nem os pecadores na comunidade dos justos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13946, 'nvi', 'Salmos', 'sl', 1, 6, 'Pois o Senhor aprova o caminho dos justos, mas o caminho dos ímpios leva à destruição!');
