INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15510, 'nvi', 'Salmos', 'sl', 100, 1, 'Aclamem o Senhor todos os habitantes da terra!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15511, 'nvi', 'Salmos', 'sl', 100, 2, 'Prestem culto ao Senhor com alegria; entrem na sua presença com cânticos alegres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15512, 'nvi', 'Salmos', 'sl', 100, 3, 'Reconheçam que ele é o nosso Deus. Ele nos fez e somos dele: somos o seu povo, e rebanho do seu pastoreio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15513, 'nvi', 'Salmos', 'sl', 100, 4, 'Entrem por suas portas com ações de graças, e em seus átrios, com louvor; dêem-lhe graças e bendigam o seu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15514, 'nvi', 'Salmos', 'sl', 100, 5, 'Pois o Senhor é bom e o seu amor leal é eterno; a sua fidelidade permanece por todas as gerações.');
