INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15515, 'nvi', 'Salmos', 'sl', 101, 1, 'Cantarei a lealdade e a justiça. A ti, Senhor, cantarei louvores!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15516, 'nvi', 'Salmos', 'sl', 101, 2, 'Seguirei o caminho da integridade; quando virás ao meu encontro? Em minha casa viverei de coração íntegro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15517, 'nvi', 'Salmos', 'sl', 101, 3, 'Repudiarei todo mal. Odeio a conduta dos infiéis; jamais me dominará!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15518, 'nvi', 'Salmos', 'sl', 101, 4, 'Longe estou dos perversos de coração; não quero envolver-me com o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15519, 'nvi', 'Salmos', 'sl', 101, 5, 'Farei calar ao que difama o próximo às ocultas. Não vou tolerar o homem de olhos arrogantes e de coração orgulhoso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15520, 'nvi', 'Salmos', 'sl', 101, 6, 'Meus olhos aprovam os fiéis da terra, e eles habitarão comigo. Somente quem tem vida íntegra me servirá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15521, 'nvi', 'Salmos', 'sl', 101, 7, 'Quem pratica a fraude não habitará no meu santuário; o mentiroso não permanecerá na minha presença.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15522, 'nvi', 'Salmos', 'sl', 101, 8, 'Cada manhã fiz calar todos os ímpios desta terra; eliminei todos os malfeitores da cidade do Senhor.');
