INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15744, 'nvi', 'Salmos', 'sl', 108, 1, 'Meu coração está firme, ó Deus! Cantarei e louvarei, ó Glória minha!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15745, 'nvi', 'Salmos', 'sl', 108, 2, 'Acordem, harpa e lira! Despertarei a alvorada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15746, 'nvi', 'Salmos', 'sl', 108, 3, 'Eu te darei graças, ó Senhor, entre os povos; cantarei louvores entre as nações,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15747, 'nvi', 'Salmos', 'sl', 108, 4, 'porque o teu amor leal se eleva muito acima dos céus; a tua fidelidade alcança as nuvens!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15748, 'nvi', 'Salmos', 'sl', 108, 5, 'Exalta-te, ó Deus, acima dos céus; estenda-se a tua glória sobre toda a terra!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15749, 'nvi', 'Salmos', 'sl', 108, 6, 'Salva-nos com a tua mão direita e responde-nos, para que sejam libertos aqueles a quem amas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15750, 'nvi', 'Salmos', 'sl', 108, 7, 'Do seu santuário Deus falou: "No meu triunfo dividirei Siquém e repartirei o vale de Sucote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15751, 'nvi', 'Salmos', 'sl', 108, 8, 'Gileade me pertence, e Manassés também; Efraim é o meu capacete, Judá é o meu cetro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15752, 'nvi', 'Salmos', 'sl', 108, 9, 'Moabe é a pia em que me lavo, em Edom atiro a minha sandália, sobre a Filístia dou meu brado de vitória! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15753, 'nvi', 'Salmos', 'sl', 108, 10, 'Quem me levará à cidade fortificada? Quem me guiará a Edom?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15754, 'nvi', 'Salmos', 'sl', 108, 11, 'Não foste tu, ó Deus, que nos rejeitaste e deixaste de sair com os nossos exércitos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15755, 'nvi', 'Salmos', 'sl', 108, 12, 'Dá-nos ajuda contra os adversários, pois inútil é o socorro do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15756, 'nvi', 'Salmos', 'sl', 108, 13, 'Com Deus conquistaremos a vitória, e ele pisará os nossos adversários.');
