INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14061, 'nvi', 'Salmos', 'sl', 11, 1, 'No Senhor me refugio. Como então vocês podem dizer-me: "Fuja como um pássaro para os montes"?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14062, 'nvi', 'Salmos', 'sl', 11, 2, 'Vejam! Os ímpios preparam os seus arcos; colocam as flechas contra as cordas para das sombras as atirarem nos retos de coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14063, 'nvi', 'Salmos', 'sl', 11, 3, 'Quando os fundamentos estão sendo destruídos, que pode fazer o justo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14064, 'nvi', 'Salmos', 'sl', 11, 4, 'O Senhor está no seu santo templo; o Senhor tem o seu trono nos céus. Seus olhos observam; seus olhos examinam os filhos dos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14065, 'nvi', 'Salmos', 'sl', 11, 5, 'O Senhor prova o justo, mas o ímpio e a quem ama a injustiça, a sua alma odeia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14066, 'nvi', 'Salmos', 'sl', 11, 6, 'Sobre os ímpios ele fará chover brasas ardentes e enxofre incandescente; vento ressecante é o que terão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14067, 'nvi', 'Salmos', 'sl', 11, 7, 'Pois o Senhor é justo, e ama a justiça; os retos verão a sua face.');
