INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15788, 'nvi', 'Salmos', 'sl', 110, 1, 'O Senhor disse ao meu Senhor: "Senta-te à minha direita até que eu faça dos teus inimigos um estrado para os teus pés".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15789, 'nvi', 'Salmos', 'sl', 110, 2, 'O Senhor estenderá o cetro de teu poder desde Sião, e dominarás sobre os teus inimigos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15790, 'nvi', 'Salmos', 'sl', 110, 3, 'Quando convocares as tuas tropas, o teu povo se apresentará voluntariamente. Trajando vestes santas, desde o romper da alvorada os teus jovens virão como o orvalho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15791, 'nvi', 'Salmos', 'sl', 110, 4, 'O Senhor jurou e não se arrependerá: "Tu és sacerdote para sempre, segundo a ordem de Melquisedeque".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15792, 'nvi', 'Salmos', 'sl', 110, 5, 'O Senhor está à tua direita; ele esmagará reis no dia da sua ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15793, 'nvi', 'Salmos', 'sl', 110, 6, 'Julgará as nações, amontoando os mortos e esmagando governantes em toda a extensão da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15794, 'nvi', 'Salmos', 'sl', 110, 7, 'No caminho beberá de um ribeiro, e então erguerá a cabeça.');
