INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15795, 'nvi', 'Salmos', 'sl', 111, 1, 'Aleluia! Darei graças ao Senhor de todo o coração na reunião da congregação dos justos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15796, 'nvi', 'Salmos', 'sl', 111, 2, 'Grandes são as obras do Senhor; nelas meditam todos os que as apreciam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15797, 'nvi', 'Salmos', 'sl', 111, 3, 'Os seus feitos manifestam majestade e esplendor, e a sua justiça dura para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15798, 'nvi', 'Salmos', 'sl', 111, 4, 'Ele fez proclamar as suas maravilhas; o Senhor é misericordioso e compassivo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15799, 'nvi', 'Salmos', 'sl', 111, 5, 'Deu alimento aos que o temiam, pois sempre se lembra de sua aliança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15800, 'nvi', 'Salmos', 'sl', 111, 6, 'Mostrou ao seu povo os seus feitos poderosos, dando-lhes as terras das nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15801, 'nvi', 'Salmos', 'sl', 111, 7, 'As obras das suas mãos são fiéis e justas; todos os seus preceitos merecem confiança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15802, 'nvi', 'Salmos', 'sl', 111, 8, 'Estão firmes para sempre, estabelecidos com fidelidade e retidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15803, 'nvi', 'Salmos', 'sl', 111, 9, 'Ele trouxe redenção ao seu povo e firmou a sua aliança para sempre. Santo e temível é o seu nome!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15804, 'nvi', 'Salmos', 'sl', 111, 10, 'O temor do Senhor é o princípio da sabedoria; todos os que cumprem os seus preceitos revelam bom senso. Ele será louvado para sempre!');
