INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15805, 'nvi', 'Salmos', 'sl', 112, 1, 'Aleluia! Como é feliz o homem que teme o Senhor e tem grande prazer em seus mandamentos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15806, 'nvi', 'Salmos', 'sl', 112, 2, 'Seus descendentes serão poderosos na terra, serão uma geração abençoada, de homens íntegros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15807, 'nvi', 'Salmos', 'sl', 112, 3, 'Grande riqueza há em sua casa, e a sua justiça dura para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15808, 'nvi', 'Salmos', 'sl', 112, 4, 'A luz raia nas trevas para o íntegro, para quem é misericordioso, compassivo e justo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15809, 'nvi', 'Salmos', 'sl', 112, 5, 'Feliz é o homem que empresta com generosidade e que com honestidade conduz os seus negócios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15810, 'nvi', 'Salmos', 'sl', 112, 6, 'O justo jamais será abalado; para sempre se lembrarão dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15811, 'nvi', 'Salmos', 'sl', 112, 7, 'Não temerá más notícias; seu coração está firme, confiante no Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15812, 'nvi', 'Salmos', 'sl', 112, 8, 'O seu coração está seguro e nada temerá. No final, verá a derrota dos seus adversários.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15813, 'nvi', 'Salmos', 'sl', 112, 9, 'Reparte generosamente com os pobres; a sua justiça dura para sempre; seu poder será exaltado em honra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15814, 'nvi', 'Salmos', 'sl', 112, 10, 'O ímpio o vê e fica irado, range os dentes e definha. O desejo dos ímpios se frustrará.');
