INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15815, 'nvi', 'Salmos', 'sl', 113, 1, 'Aleluia! Louvem, ó servos do Senhor, louvem o nome do Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15816, 'nvi', 'Salmos', 'sl', 113, 2, 'Seja bendito o nome do Senhor, desde agora e para sempre!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15817, 'nvi', 'Salmos', 'sl', 113, 3, 'Do nascente ao poente, seja louvado o nome do Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15818, 'nvi', 'Salmos', 'sl', 113, 4, 'O Senhor está exaltado acima de todas as nações; e acima dos céus está a sua glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15819, 'nvi', 'Salmos', 'sl', 113, 5, 'Quem é como o Senhor, o nosso Deus, que reina em seu trono nas alturas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15820, 'nvi', 'Salmos', 'sl', 113, 6, 'mas se inclina para contemplar o que acontece nos céus e na terra?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15821, 'nvi', 'Salmos', 'sl', 113, 7, 'Ele levanta do pó o necessitado e ergue do lixo o pobre,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15822, 'nvi', 'Salmos', 'sl', 113, 8, 'para fazê-los sentar-se com príncipes, com os príncipes do seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15823, 'nvi', 'Salmos', 'sl', 113, 9, 'Dá um lar à estéril, e dela faz uma feliz mãe de filhos. Aleluia!');
