INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15824, 'nvi', 'Salmos', 'sl', 114, 1, 'Quando Israel saiu do Egito, e a casa de Jacó saiu do meio de um povo de língua estrangeira,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15825, 'nvi', 'Salmos', 'sl', 114, 2, 'Judá tornou-se o santuário de Deus, Israel o seu domínio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15826, 'nvi', 'Salmos', 'sl', 114, 3, 'O mar olhou e fugiu, o Jordão retrocedeu;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15827, 'nvi', 'Salmos', 'sl', 114, 4, 'os montes saltaram como carneiros, as colinas, como cordeiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15828, 'nvi', 'Salmos', 'sl', 114, 5, 'Por que fugir, ó mar? E você, Jordão, por que retroceder?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15829, 'nvi', 'Salmos', 'sl', 114, 6, 'Por que vocês saltaram como carneiros, ó montes? E vocês, colinas, porque saltaram como cordeiros?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15830, 'nvi', 'Salmos', 'sl', 114, 7, 'Estremeça na presença do Soberano, ó terra, na presença do Deus de Jacó!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15831, 'nvi', 'Salmos', 'sl', 114, 8, 'Ele fez da rocha um açude, do rochedo uma fonte.');
