INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15832, 'nvi', 'Salmos', 'sl', 115, 1, 'Não a nós, Senhor, nenhuma glória para nós, mas sim ao teu nome, por teu amor e por tua fidelidade!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15833, 'nvi', 'Salmos', 'sl', 115, 2, 'Por que perguntam as nações: "Onde está o Deus deles? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15834, 'nvi', 'Salmos', 'sl', 115, 3, 'O nosso Deus está nos céus, e pode fazer tudo o que lhe agrada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15835, 'nvi', 'Salmos', 'sl', 115, 4, 'Os ídolos deles, de prata e ouro, são feitos por mãos humanas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15836, 'nvi', 'Salmos', 'sl', 115, 5, 'Têm boca, mas não podem falar, olhos, mas não podem ver;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15837, 'nvi', 'Salmos', 'sl', 115, 6, 'têm ouvidos, mas não podem ouvir, nariz, mas não podem sentir cheiro;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15838, 'nvi', 'Salmos', 'sl', 115, 7, 'têm mãos, mas nada podem apalpar, pés, mas não podem andar; nem emitem som algum com a garganta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15839, 'nvi', 'Salmos', 'sl', 115, 8, 'Tornem-se como eles aqueles que os fazem e todos os que neles confiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15840, 'nvi', 'Salmos', 'sl', 115, 9, 'Confie no Senhor, ó Israel! Ele é o seu socorro e o seu escudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15841, 'nvi', 'Salmos', 'sl', 115, 10, 'Confiem no Senhor, sacerdotes! Ele é o seu socorro e o seu escudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15842, 'nvi', 'Salmos', 'sl', 115, 11, 'Vocês que temem ao Senhor, confiem no Senhor! Ele é o seu socorro e o seu escudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15843, 'nvi', 'Salmos', 'sl', 115, 12, 'O Senhor lembra-se de nós e nos abençoará: Abençoará os israelitas, abençoará os sacerdotes,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15844, 'nvi', 'Salmos', 'sl', 115, 13, 'abençoará os que temem o Senhor, do menor ao maior.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15845, 'nvi', 'Salmos', 'sl', 115, 14, 'Que o Senhor os multiplique, a vocês e aos seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15846, 'nvi', 'Salmos', 'sl', 115, 15, 'Sejam vocês abençoados pelo Senhor, que fez os céus e a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15847, 'nvi', 'Salmos', 'sl', 115, 16, 'Os mais altos céus pertencem ao Senhor, mas a terra ele a confiou ao homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15848, 'nvi', 'Salmos', 'sl', 115, 17, 'Os mortos não louvam o Senhor, tampouco nenhum dos que descem ao silêncio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15849, 'nvi', 'Salmos', 'sl', 115, 18, 'Mas nós bendiremos o Senhor, desde agora e para sempre! Aleluia!');
