INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14068, 'nvi', 'Salmos', 'sl', 12, 1, 'Salva-nos, Senhor! Já não há quem seja fiel; já não se confia em ninguém entre os homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14069, 'nvi', 'Salmos', 'sl', 12, 2, 'Cada um mente ao seu próximo; seus lábios bajuladores falam com segundas intenções.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14070, 'nvi', 'Salmos', 'sl', 12, 3, 'Que o Senhor corte todos os lábios bajuladores e toda língua arrogante');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14071, 'nvi', 'Salmos', 'sl', 12, 4, 'dos que dizem: "Venceremos graças à nossa língua; somos donos dos nossos lábios! Quem é senhor sobre nós? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14072, 'nvi', 'Salmos', 'sl', 12, 5, '"Por causa da opressão do necessitado e do gemido do pobre, agora me levantarei", diz o Senhor. "Eu lhes darei a segurança que tanto anseiam. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14073, 'nvi', 'Salmos', 'sl', 12, 6, 'As palavras do Senhor são puras, são como prata purificada num forno, sete vezes refinada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14074, 'nvi', 'Salmos', 'sl', 12, 7, 'Senhor, tu nos guardarás seguros, e dessa gente nos protegerás para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14075, 'nvi', 'Salmos', 'sl', 12, 8, 'Os ímpios andam altivos por toda parte, quando a corrupção é exaltada entre os homens.');
