INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16076, 'nvi', 'Salmos', 'sl', 120, 1, 'Eu clamo pelo Senhor na minha angústia, e ele me responde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16077, 'nvi', 'Salmos', 'sl', 120, 2, 'Senhor, livra-me dos lábios mentirosos e da língua traiçoeira!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16078, 'nvi', 'Salmos', 'sl', 120, 3, 'O que ele lhe dará? Como lhe retribuirá, ó língua enganadora?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16079, 'nvi', 'Salmos', 'sl', 120, 4, 'Ele a castigará com flechas afiadas de guerreiro, com brasas incandescentes de sândalo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16080, 'nvi', 'Salmos', 'sl', 120, 5, 'Ai de mim que vivo como estrangeiro em Meseque, que habito entre as tendas de Quedar!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16081, 'nvi', 'Salmos', 'sl', 120, 6, 'Tenho vivido tempo demais entre os que odeiam a paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16082, 'nvi', 'Salmos', 'sl', 120, 7, 'Sou um homem de paz; mas, ainda que eu fale de paz, eles só falam de guerra.');
