INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16083, 'nvi', 'Salmos', 'sl', 121, 1, 'Levanto os meus olhos para os montes e pergunto: De onde me vem o socorro?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16084, 'nvi', 'Salmos', 'sl', 121, 2, 'O meu socorro vem do Senhor, que fez os céus e a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16085, 'nvi', 'Salmos', 'sl', 121, 3, 'Ele não permitirá que você tropece; o seu protetor se manterá alerta,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16086, 'nvi', 'Salmos', 'sl', 121, 4, 'sim, o protetor de Israel não dormirá, ele está sempre alerta!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16087, 'nvi', 'Salmos', 'sl', 121, 5, 'O Senhor é o seu protetor; como sombra que o protege, ele está à sua direita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16088, 'nvi', 'Salmos', 'sl', 121, 6, 'De dia o sol não o ferirá, nem a lua, de noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16089, 'nvi', 'Salmos', 'sl', 121, 7, 'O Senhor o protegerá de todo o mal, protegerá a sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16090, 'nvi', 'Salmos', 'sl', 121, 8, 'O Senhor protegerá a sua saída e a sua chegada, desde agora e para sempre.');
