INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16091, 'nvi', 'Salmos', 'sl', 122, 1, 'Alegrei-me com os que me disseram: "Vamos à casa do Senhor! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16092, 'nvi', 'Salmos', 'sl', 122, 2, 'Nossos pés já se encontram dentro de suas portas, ó Jerusalém!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16093, 'nvi', 'Salmos', 'sl', 122, 3, 'Jerusalém está construída como cidade firmemente estabelecida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16094, 'nvi', 'Salmos', 'sl', 122, 4, 'Para lá sobem as tribos do Senhor, para dar graças ao Senhor, conforme o mandamento dado a Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16095, 'nvi', 'Salmos', 'sl', 122, 5, 'Lá estão os tribunais de justiça, os tribunais da casa real de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16096, 'nvi', 'Salmos', 'sl', 122, 6, 'Orem pela paz de Jerusalém: "Vivam em segurança aqueles que te amam!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16097, 'nvi', 'Salmos', 'sl', 122, 7, 'Haja paz dentro dos teus muros e segurança nas tuas cidadelas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16098, 'nvi', 'Salmos', 'sl', 122, 8, 'Em favor de meus irmãos e amigos, direi: "Paz seja com você! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16099, 'nvi', 'Salmos', 'sl', 122, 9, 'Em favor da casa do Senhor, nosso Deus, buscarei o seu bem.');
