INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16100, 'nvi', 'Salmos', 'sl', 123, 1, 'Para ti levanto os meus olhos, a ti, que ocupas o teu trono nos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16101, 'nvi', 'Salmos', 'sl', 123, 2, 'Assim como os olhos dos servos estão atentos à mão de seu senhor, e como os olhos das servas estão atentos à mão de sua senhora, também os nossos olhos estão atentos ao Senhor, ao nosso Deus, esperando que ele tenha misericórdia de nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16102, 'nvi', 'Salmos', 'sl', 123, 3, 'Misericórdia, Senhor! Tem misericórdia de nós! Já estamos cansados de tanto desprezo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16103, 'nvi', 'Salmos', 'sl', 123, 4, 'Estamos cansados de tanta zombaria dos orgulhosos e do desprezo dos arrogantes.');
