INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16104, 'nvi', 'Salmos', 'sl', 124, 1, 'Se o Senhor não estivesse do nosso lado; que Israel o repita:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16105, 'nvi', 'Salmos', 'sl', 124, 2, 'Se o Senhor não estivesse do nosso lado quando os inimigos nos atacaram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16106, 'nvi', 'Salmos', 'sl', 124, 3, 'eles já nos teriam engolido vivos, quando se enfureceram contra nós;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16107, 'nvi', 'Salmos', 'sl', 124, 4, 'as águas nos teriam arrastado e as torrentes nos teriam afogado;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16108, 'nvi', 'Salmos', 'sl', 124, 5, 'sim, as águas violentas nos teriam afogado!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16109, 'nvi', 'Salmos', 'sl', 124, 6, 'Bendito seja o Senhor, que não nos entregou para sermos dilacerados pelos dentes deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16110, 'nvi', 'Salmos', 'sl', 124, 7, 'Como um pássaro escapamos da armadilha do caçador; a armadilha foi quebrada, e nós escapamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16111, 'nvi', 'Salmos', 'sl', 124, 8, 'O nosso socorro está no nome do Senhor, que fez os céus e a terra.');
