INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16112, 'nvi', 'Salmos', 'sl', 125, 1, 'Os que confiam no Senhor são como o monte Sião, que não se pode abalar, mas permanece para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16113, 'nvi', 'Salmos', 'sl', 125, 2, 'Como os montes cercam Jerusalém, assim o Senhor protege o seu povo, desde agora e para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16114, 'nvi', 'Salmos', 'sl', 125, 3, 'O cetro dos ímpios não prevalecerá sobre a terra dada aos justos, se assim fosse, até os justos praticariam a injustiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16115, 'nvi', 'Salmos', 'sl', 125, 4, 'Senhor, trata com bondade aos que fazem o bem, aos que têm coração íntegro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16116, 'nvi', 'Salmos', 'sl', 125, 5, 'Mas aos que se desviam por caminhos tortuosos, o Senhor dará o castigo dos malfeitores. Haja paz em Israel!');
