INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16117, 'nvi', 'Salmos', 'sl', 126, 1, 'Quando o Senhor trouxe os cativos de volta a Sião, foi como um sonho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16118, 'nvi', 'Salmos', 'sl', 126, 2, 'Então a nossa boca encheu-se de riso, e a nossa língua de cantos de alegria. Até nas outras nações se dizia: "O Senhor fez coisas grandiosas por este povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16119, 'nvi', 'Salmos', 'sl', 126, 3, 'Sim, coisas grandiosas fez o Senhor por nós, por isso estamos alegres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16120, 'nvi', 'Salmos', 'sl', 126, 4, 'Senhor, restaura-nos, assim como enches o leito dos ribeiros no deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16121, 'nvi', 'Salmos', 'sl', 126, 5, 'Aqueles que semeiam com lágrimas, com cantos de alegria colherão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16122, 'nvi', 'Salmos', 'sl', 126, 6, 'Aquele que sai chorando enquanto lança a semente, voltará com cantos de alegria, trazendo os seus feixes.');
