INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16123, 'nvi', 'Salmos', 'sl', 127, 1, 'Se não for o Senhor o construtor da casa, será inútil trabalhar na construção. Se não é o Senhor que vigia a cidade, será inútil a sentinela montar guarda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16124, 'nvi', 'Salmos', 'sl', 127, 2, 'Será inútil levantar cedo e dormir tarde, trabalhando arduamente por alimento. O Senhor concede o sono àqueles a quem ama.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16125, 'nvi', 'Salmos', 'sl', 127, 3, 'Os filhos são herança do Senhor, uma recompensa que ele dá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16126, 'nvi', 'Salmos', 'sl', 127, 4, 'Como flechas nas mãos do guerreiro são os filhos nascidos na juventude.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16127, 'nvi', 'Salmos', 'sl', 127, 5, 'Como é feliz o homem cuja aljava está cheia deles! Não será humilhado quando enfrentar seus inimigos no tribunal.');
