INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16128, 'nvi', 'Salmos', 'sl', 128, 1, 'Como é feliz quem teme ao Senhor, quem anda em seus caminhos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16129, 'nvi', 'Salmos', 'sl', 128, 2, 'Você comerá do fruto do seu trabalho, e será feliz e próspero.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16130, 'nvi', 'Salmos', 'sl', 128, 3, 'Sua mulher será como videira frutífera em sua casa; seus filhos serão como brotos de oliveira ao redor da sua mesa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16131, 'nvi', 'Salmos', 'sl', 128, 4, 'Assim será abençoado o homem que teme ao Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16132, 'nvi', 'Salmos', 'sl', 128, 5, 'Que o Senhor o abençoe desde Sião, para que você veja a prosperidade de Jerusalém todos os dias da sua vida,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16133, 'nvi', 'Salmos', 'sl', 128, 6, 'e veja os filhos dos seus filhos. Haja paz em Israel!');
