INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16134, 'nvi', 'Salmos', 'sl', 129, 1, 'Muitas vezes me oprimiram desde a minha juventude; que Israel o repita:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16135, 'nvi', 'Salmos', 'sl', 129, 2, 'muitas vezes me oprimiram desde a minha juventude, mas jamais conseguiram vencer-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16136, 'nvi', 'Salmos', 'sl', 129, 3, 'Passaram o arado em minhas costas e fizeram longos sulcos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16137, 'nvi', 'Salmos', 'sl', 129, 4, 'O Senhor é justo! Ele libertou-me das algemas dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16138, 'nvi', 'Salmos', 'sl', 129, 5, 'Retrocedam envergonhados todos os que odeiam Sião.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16139, 'nvi', 'Salmos', 'sl', 129, 6, 'Sejam como o capim do terraço, que seca antes de crescer,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16140, 'nvi', 'Salmos', 'sl', 129, 7, 'que não enche as mãos do ceifeiro nem os braços daquele que faz os fardos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16141, 'nvi', 'Salmos', 'sl', 129, 8, 'E que ninguém que passa diga: "Seja sobre vocês a bênção do Senhor; nós os abençoamos em nome do Senhor! "');
