INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14076, 'nvi', 'Salmos', 'sl', 13, 1, 'Até quando, Senhor? Para sempre te esquecerás de mim? Até quando esconderás de mim o teu rosto?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14077, 'nvi', 'Salmos', 'sl', 13, 2, 'Até quando terei inquietações e tristeza no coração dia após dia? Até quando o meu inimigo triunfará sobre mim?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14078, 'nvi', 'Salmos', 'sl', 13, 3, 'Olha para mim e responde, Senhor meu Deus. Ilumina os meus olhos, do contrário dormirei o sono da morte;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14079, 'nvi', 'Salmos', 'sl', 13, 4, 'os meus inimigos dirão: "Eu o venci", e os meus adversários festejarão o meu fracasso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14080, 'nvi', 'Salmos', 'sl', 13, 5, 'Eu, porém, confio em teu amor; o meu coração exulta em tua salvação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14081, 'nvi', 'Salmos', 'sl', 13, 6, 'Quero cantar ao Senhor pelo bem que me tem feito.');
