INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16142, 'nvi', 'Salmos', 'sl', 130, 1, 'Das profundezas clamo a ti, Senhor;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16143, 'nvi', 'Salmos', 'sl', 130, 2, 'Ouve, Senhor, a minha voz! Estejam atentos os teus ouvidos às minhas súplicas!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16144, 'nvi', 'Salmos', 'sl', 130, 3, 'Se tu, Soberano Senhor, registrasses os pecados, quem escaparia?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16145, 'nvi', 'Salmos', 'sl', 130, 4, 'Mas contigo está o perdão para que sejas temido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16146, 'nvi', 'Salmos', 'sl', 130, 5, 'Espero no Senhor com todo o meu ser, e na sua palavra ponho a minha esperança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16147, 'nvi', 'Salmos', 'sl', 130, 6, 'Espero pelo Senhor mais do que as sentinelas pela manhã; sim, mais do que as sentinelas esperam pela manhã!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16148, 'nvi', 'Salmos', 'sl', 130, 7, 'Ponha a sua esperança no Senhor, ó Israel, pois no Senhor há amor leal e plena redenção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16149, 'nvi', 'Salmos', 'sl', 130, 8, 'Ele próprio redimirá Israel de todas as suas culpas.');
