INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16150, 'nvi', 'Salmos', 'sl', 131, 1, 'Senhor, o meu coração não é orgulhoso e os meus olhos não são arrogantes. Não me envolvo com coisas grandiosas nem maravilhosas demais para mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16151, 'nvi', 'Salmos', 'sl', 131, 2, 'De fato, acalmei e tranqüilizei a minha alma. Sou como uma criança recém-amamentada por sua mãe; a minha alma é como essa criança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16152, 'nvi', 'Salmos', 'sl', 131, 3, 'Ponha a sua esperança no Senhor, ó Israel, desde agora e para sempre!');
