INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16153, 'nvi', 'Salmos', 'sl', 132, 1, 'Senhor, lembra-te de Davi e das dificuldades que enfrentou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16154, 'nvi', 'Salmos', 'sl', 132, 2, 'Ele jurou ao Senhor e fez um voto ao Poderoso de Jacó:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16155, 'nvi', 'Salmos', 'sl', 132, 3, '"Não entrarei na minha tenda e não me deitarei no meu leito;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16156, 'nvi', 'Salmos', 'sl', 132, 4, 'não permitirei que os meus olhos peguem no sono nem que as minhas pálpebras descansem,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16157, 'nvi', 'Salmos', 'sl', 132, 5, 'enquanto não encontrar um lugar para o Senhor, uma habitação para o Poderoso de Jacó".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16158, 'nvi', 'Salmos', 'sl', 132, 6, 'Soubemos que a arca estava em Efrata, mas nós a encontramos nos campos de Jaar:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16159, 'nvi', 'Salmos', 'sl', 132, 7, '"Vamos para habitação dele! Vamos adorá-lo diante do estrado de seus pés!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16160, 'nvi', 'Salmos', 'sl', 132, 8, 'Levanta-te, Senhor, e vem para o teu lugar de descanso, tu e a arca onde está o teu poder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16161, 'nvi', 'Salmos', 'sl', 132, 9, 'Vistam-se de retidão os teus sacerdotes; cantem de alegria os teus fiéis".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16162, 'nvi', 'Salmos', 'sl', 132, 10, 'Por amor ao teu servo Davi, não rejeites o teu ungido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16163, 'nvi', 'Salmos', 'sl', 132, 11, 'O Senhor fez um juramento a Davi, um juramento firme que ele não revogará: "Colocarei um dos seus descendentes no seu trono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16164, 'nvi', 'Salmos', 'sl', 132, 12, 'Se os seus filhos forem fiéis à minha aliança e aos testemunhos que eu lhes ensino, também os filhos deles o sucederão no trono para sempre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16165, 'nvi', 'Salmos', 'sl', 132, 13, 'O Senhor escolheu Sião, com o desejo de fazê-la sua habitação:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16166, 'nvi', 'Salmos', 'sl', 132, 14, '"Este será o meu lugar de descanso para sempre; aqui firmarei o meu trono, pois esse é o meu desejo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16167, 'nvi', 'Salmos', 'sl', 132, 15, 'Abençoarei este lugar com fartura; os seus pobres suprirei de pão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16168, 'nvi', 'Salmos', 'sl', 132, 16, 'Vestirei de salvação os seus sacerdotes e os seus fiéis a celebrarão com grande alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16169, 'nvi', 'Salmos', 'sl', 132, 17, '"Ali farei renascer o poder de Davi e farei brilhar a luz do meu ungido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16170, 'nvi', 'Salmos', 'sl', 132, 18, 'Vestirei de vergonha os seus inimigos, mas nele brilhará a sua coroa".');
