INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16171, 'nvi', 'Salmos', 'sl', 133, 1, 'Como é bom e agradável quando os irmãos convivem em união!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16172, 'nvi', 'Salmos', 'sl', 133, 2, 'É como óleo precioso derramado sobre a cabeça, que desce pela barba, a barba de Arão, até a gola das suas vestes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16173, 'nvi', 'Salmos', 'sl', 133, 3, 'É como o orvalho do Hermom quando desce sobre os montes de Sião. Ali o Senhor concede a bênção da vida para sempre.');
