INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16224, 'nvi', 'Salmos', 'sl', 137, 1, 'Junto aos rios da Babilônia nós nos sentamos e choramos com saudade de Sião.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16225, 'nvi', 'Salmos', 'sl', 137, 2, 'Ali, nos salgueiros penduramos as nossas harpas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16226, 'nvi', 'Salmos', 'sl', 137, 3, 'ali os nossos captores pediam-nos canções, os nossos opressores exigiam canções alegres, dizendo: "Cantem para nós uma das canções de Sião! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16227, 'nvi', 'Salmos', 'sl', 137, 4, 'Como poderíamos cantar as canções do Senhor numa terra estrangeira?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16228, 'nvi', 'Salmos', 'sl', 137, 5, 'Que a minha mão direita definhe, ó Jerusalém, se eu me esquecer de ti!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16229, 'nvi', 'Salmos', 'sl', 137, 6, 'Que a língua se me grude ao céu da boca, se eu não me lembrar de ti, e não considerar Jerusalém a minha maior alegria!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16230, 'nvi', 'Salmos', 'sl', 137, 7, 'Lembra-te, Senhor, dos edomitas e do que fizeram quando Jerusalém foi destruída, pois gritavam: "Arrasem-na! Arrasem-na até aos alicerces! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16231, 'nvi', 'Salmos', 'sl', 137, 8, 'Ó cidade de Babilônia, destinada à destruição, feliz aquele que lhe retribuir o mal que você nos fez!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16232, 'nvi', 'Salmos', 'sl', 137, 9, 'Feliz aquele que pegar os seus filhos e os despedaçar contra a rocha!');
