INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14082, 'nvi', 'Salmos', 'sl', 14, 1, 'Diz o tolo em seu coração: "Deus não existe". Corromperam-se e cometeram atos detestáveis; não há ninguém que faça o bem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14083, 'nvi', 'Salmos', 'sl', 14, 2, 'O Senhor olha dos céus para os filhos dos homens, para ver se há alguém que tenha entendimento, alguém que busque a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14084, 'nvi', 'Salmos', 'sl', 14, 3, 'Todos se desviaram, igualmente se corromperam; não há ninguém que faça o bem, não há nem um sequer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14085, 'nvi', 'Salmos', 'sl', 14, 4, 'Será que nenhum dos malfeitores aprende? Eles devoram o meu povo como quem come pão, e não clamam pelo Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14086, 'nvi', 'Salmos', 'sl', 14, 5, 'Olhem! Estão tomados de pavor! Pois Deus está presente no meio dos justos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14087, 'nvi', 'Salmos', 'sl', 14, 6, 'Vocês, malfeitores, frustram os planos dos pobres, mas o refúgio deles é o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14088, 'nvi', 'Salmos', 'sl', 14, 7, 'Ah, se de Sião viesse a salvação para Israel! Quando o Senhor restaurar o seu povo, Jacó exultará! Israel se regozijará!');
