INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16265, 'nvi', 'Salmos', 'sl', 140, 1, 'Livra-me, Senhor, dos maus; protege-me dos violentos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16266, 'nvi', 'Salmos', 'sl', 140, 2, 'que no coração tramam planos perversos e estão sempre provocando guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16267, 'nvi', 'Salmos', 'sl', 140, 3, 'Afiam a língua como a da serpente; veneno de víbora está em seus lábios. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16268, 'nvi', 'Salmos', 'sl', 140, 4, 'Protege-me, Senhor, das mãos dos ímpios; protege-me dos violentos, que pretendem fazer-me tropeçar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16269, 'nvi', 'Salmos', 'sl', 140, 5, 'Homens arrogantes prepararam armadilhas contra mim, perversos estenderam as suas redes; no meu caminho armaram ciladas contra mim. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16270, 'nvi', 'Salmos', 'sl', 140, 6, 'Eu declaro ao Senhor: "Tu és o meu Deus". Ouve, Senhor, a minha súplica!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16271, 'nvi', 'Salmos', 'sl', 140, 7, 'Ó Soberano Senhor, meu salvador poderoso, tu me proteges a cabeça no dia da batalha;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16272, 'nvi', 'Salmos', 'sl', 140, 8, 'não atendas os desejos dos ímpios, Senhor! Não permitas que os planos deles tenham sucesso, para que não se orgulhem. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16273, 'nvi', 'Salmos', 'sl', 140, 9, 'Recaia sobre a cabeça dos que me cercam a maldade que os seus lábios proferiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16274, 'nvi', 'Salmos', 'sl', 140, 10, 'Caiam brasas sobre eles, e sejam lançados ao fogo, em covas das quais jamais possam sair.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16275, 'nvi', 'Salmos', 'sl', 140, 11, 'Que os difamadores não se estabeleçam na terra, e a desgraça persiga os violentos até à morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16276, 'nvi', 'Salmos', 'sl', 140, 12, 'Sei que o Senhor defenderá a causa do necessitado e fará justiça aos pobres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16277, 'nvi', 'Salmos', 'sl', 140, 13, 'Com certeza os justos darão graças ao teu nome, e os homens íntegros viverão na tua presença.');
