INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16278, 'nvi', 'Salmos', 'sl', 141, 1, 'Clamo a ti, Senhor; vem depressa! Escuta a minha voz quando clamo a ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16279, 'nvi', 'Salmos', 'sl', 141, 2, 'Seja a minha oração como incenso diante de ti, e o levantar das minhas mãos, como a oferta da tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16280, 'nvi', 'Salmos', 'sl', 141, 3, 'Coloca, Senhor, uma guarda à minha boca; vigia a porta de meus lábios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16281, 'nvi', 'Salmos', 'sl', 141, 4, 'Não permitas que o meu coração se volte para o mal, nem que eu me envolva em práticas perversas com os malfeitores. Que eu nunca participe dos seus banquetes!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16282, 'nvi', 'Salmos', 'sl', 141, 5, 'Fira-me o justo com amor leal e me repreenda, mas não perfume a minha cabeça o óleo do ímpio, pois a minha oração é contra as práticas dos malfeitores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16283, 'nvi', 'Salmos', 'sl', 141, 6, 'Quando eles caírem nas mãos da Rocha, o juiz deles, ouvirão as minhas palavras com apreço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16284, 'nvi', 'Salmos', 'sl', 141, 7, 'Como a terra é arada e fendida, assim foram espalhados os seus ossos à entrada da sepultura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16285, 'nvi', 'Salmos', 'sl', 141, 8, 'Mas os meus olhos estão fixos em ti, ó Soberano Senhor; em ti me refugio; não me entregues à morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16286, 'nvi', 'Salmos', 'sl', 141, 9, 'Guarda-me das armadilhas que prepararam contra mim, das ciladas dos que praticam o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16287, 'nvi', 'Salmos', 'sl', 141, 10, 'Caiam os ímpios em sua própria rede, enquanto eu escapo ileso.');
