INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16343, 'nvi', 'Salmos', 'sl', 146, 1, 'Aleluia! Louve, ó minha alma ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16344, 'nvi', 'Salmos', 'sl', 146, 2, 'Louvarei ao Senhor por toda a minha vida; cantarei louvores ao meu Deus enquanto eu viver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16345, 'nvi', 'Salmos', 'sl', 146, 3, 'Não confiem em príncipes, em meros mortais, incapazes de salvar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16346, 'nvi', 'Salmos', 'sl', 146, 4, 'Quando o espírito deles se vai, voltam ao pó; naquele mesmo dia acabam-se os seus planos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16347, 'nvi', 'Salmos', 'sl', 146, 5, 'Como é feliz aquele cujo auxílio é o Deus de Jacó, cuja esperança está no Senhor, no seu Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16348, 'nvi', 'Salmos', 'sl', 146, 6, 'que fez os céus e a terra, o mar e tudo o que neles há, e que mantém a sua fidelidade para sempre!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16349, 'nvi', 'Salmos', 'sl', 146, 7, 'Ele defende a causa dos oprimidos e dá alimento aos famintos. O Senhor liberta os presos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16350, 'nvi', 'Salmos', 'sl', 146, 8, 'o Senhor dá vista aos cegos, o Senhor levanta os abatidos, o Senhor ama os justos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16351, 'nvi', 'Salmos', 'sl', 146, 9, 'O Senhor protege o estrangeiro e sustém o órfão e a viúva, mas frustra o propósito dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16352, 'nvi', 'Salmos', 'sl', 146, 10, 'O Senhor reina para sempre! O teu Deus, ó Sião, reina de geração em geração. Aleluia!');
