INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16373, 'nvi', 'Salmos', 'sl', 148, 1, 'Aleluia! Louvem o Senhor desde os céus, louvem-no nas alturas!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16374, 'nvi', 'Salmos', 'sl', 148, 2, 'Louvem-no todos os seus anjos, louvem-no todos os seus exércitos celestiais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16375, 'nvi', 'Salmos', 'sl', 148, 3, 'Louvem-no sol e lua, louvem-no todas as estrelas cintilantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16376, 'nvi', 'Salmos', 'sl', 148, 4, 'Louvem-no os mais altos céus e as águas acima do firmamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16377, 'nvi', 'Salmos', 'sl', 148, 5, 'Louvem todos eles o nome do Senhor, pois ordenou, e eles foram criados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16378, 'nvi', 'Salmos', 'sl', 148, 6, 'Ele os estabeleceu em seus lugares para todo o sempre; deu-lhes um decreto que jamais mudará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16379, 'nvi', 'Salmos', 'sl', 148, 7, 'Louvem o Senhor, vocês que estão na terra, serpentes marinhas e todas as profundezas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16380, 'nvi', 'Salmos', 'sl', 148, 8, 'relâmpagos e granizo, neve e neblina, vendavais que cumprem o que ele determina,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16381, 'nvi', 'Salmos', 'sl', 148, 9, 'todas as montanhas e colinas, árvores frutíferas e todos os cedros,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16382, 'nvi', 'Salmos', 'sl', 148, 10, 'todos os animais selvagens e os rebanhos domésticos, todos os demais seres vivos e as aves,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16383, 'nvi', 'Salmos', 'sl', 148, 11, 'reis da terra e todas as nações, todos os governantes e juízes da terra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16384, 'nvi', 'Salmos', 'sl', 148, 12, 'moços e moças, velhos e crianças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16385, 'nvi', 'Salmos', 'sl', 148, 13, 'Louvem todos o nome do Senhor, pois somente o seu nome é exaltado; a sua majestade está acima da terra e dos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16386, 'nvi', 'Salmos', 'sl', 148, 14, 'Ele concedeu poder ao seu povo, e recebeu louvor de todos os seus fiéis, dos israelitas, povo a quem tanto ama. Aleluia!');
