INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16387, 'nvi', 'Salmos', 'sl', 149, 1, 'Aleluia! Cantem ao Senhor uma nova canção, louvem-no na assembléia dos fiéis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16388, 'nvi', 'Salmos', 'sl', 149, 2, 'Alegre-se Israel no seu criador, exulte o povo de Sião no seu rei!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16389, 'nvi', 'Salmos', 'sl', 149, 3, 'Louvem eles o seu nome com danças; ofereçam-lhe música com tamborim e harpa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16390, 'nvi', 'Salmos', 'sl', 149, 4, 'O Senhor agrada-se do seu povo; ele coroa de vitória os oprimidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16391, 'nvi', 'Salmos', 'sl', 149, 5, 'Regozijem-se os seus fiéis nessa glória e em seus leitos cantem alegremente!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16392, 'nvi', 'Salmos', 'sl', 149, 6, 'Altos louvores estejam em seus lábios e uma espada de dois gumes em suas mãos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16393, 'nvi', 'Salmos', 'sl', 149, 7, 'para imporem vingança às nações e trazerem castigo aos povos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16394, 'nvi', 'Salmos', 'sl', 149, 8, 'para prenderem os seus reis com grilhões e seus nobres com algemas de ferro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16395, 'nvi', 'Salmos', 'sl', 149, 9, 'para executarem a sentença escrita contra eles. Esta é a glória de todos os seus fiéis. Aleluia!');
