INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14089, 'nvi', 'Salmos', 'sl', 15, 1, 'Senhor, quem habitará no teu santuário? Quem poderá morar no teu santo monte?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14090, 'nvi', 'Salmos', 'sl', 15, 2, 'Aquele que é íntegro em sua conduta e pratica o que é justo, que de coração fala a verdade');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14091, 'nvi', 'Salmos', 'sl', 15, 3, 'e não usa a língua para difamar, que nenhum mal faz ao seu semelhante e não lança calúnia contra o seu próximo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14092, 'nvi', 'Salmos', 'sl', 15, 4, 'que rejeita quem merece desprezo, mas honra os que temem ao Senhor, que mantém a sua palavra, mesmo quando sai prejudicado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14093, 'nvi', 'Salmos', 'sl', 15, 5, 'que não empresta o seu dinheiro visando lucro nem aceita suborno contra o inocente. Quem assim procede nunca será abalado!');
